INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (26, 'Юна черкащанка перемогла на конкурсі дитячого малюнку в Японії', '0d6b1cb63e23f80cd48e084f7843cd82', 'Юна черкащанка перемогла на конкурсі дитячого малюнку в Японії', 'Маленька черкащанка Марія Бодашко здобула перемогу на 20-му Міжнародному конкурсі дитячого малюнку бієнале у префектурі Канагава Японії. Дівчинка нагороджена Призом Канагави. Про це повідомляють на сайті Черкаської міської ради. Конкурс існує із 1981 року та об’єднує дітей із понад 100 країн світу. Організатор – Префектура Канагава при підтримці багатьох організацій у тому числі і Національної Федерації Асоціацій ЮНЕСКО Японії. Цьогоріч на конкурс було подано понад 27 тисяч малюнків із 92 країн. Конкурс сприяє формуванню уявлень дітей про розмаїття побуту і культурних традицій жителів різних куточків планети і ставить завданням розвиток дитячої творчої уяви та художніх здібностей.', '<p class=\"bb_p\"><b class=\"bb_b\">Маленька черкащанка Марія Бодашко здобула перемогу на 20-му Міжнародному конкурсі дитячого малюнку бієнале у префектурі Канагава Японії. Дівчинка нагороджена Призом Канагави.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-38-33_990.png" alt="" title="" /></p>
<p class=\"bb_p\">Про це повідомляють на сайті Черкаської міської ради.</p>
<p class=\"bb_p\">Конкурс існує із 1981 року та об’єднує дітей із понад 100 країн світу. Організатор – Префектура Канагава при підтримці багатьох організацій у тому числі і Національної Федерації Асоціацій ЮНЕСКО Японії.</p>
<p class=\"bb_p\">Цьогоріч на конкурс було подано понад 27 тисяч малюнків із 92 країн. Конкурс сприяє формуванню уявлень дітей про розмаїття побуту і культурних традицій жителів різних куточків планети і ставить завданням розвиток дитячої творчої уяви та художніх здібностей.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://18000.com.ua/strichka-novin/yuna-cherkashhanka-peremogla-na-konkursi-dityachogo-malyunku-v-yaponi%d1%97-foto/" target="_blank">За матеріалами "18000.com.ua"</a></p></div>', 1, '2019-10-31 20:38:39.30245', '''100'':83 ''18000.com.ua'':151 ''1981'':75 ''20'':35 ''27'':112 ''92'':116 ''align'':54,147 ''b'':24,27 ''bb'':22,26,52,58,70,104,145 ''center'':55 ''class'':21,25,51,57,69,103,144 ''p'':20,23,50,53,56,59,68,71,102,105,143,146 ''right'':148 ''асоціаці'':99 ''багатьох'':91 ''бодашк'':31 ''бул'':109 ''бієнал'':41 ''дитяч'':7B,16C,39 ''дитячої'':137 ''дівчинк'':46 ''діте'':80,122 ''жителів'':129 ''завдан'':135 ''здобул'':32 ''здібност'':142 ''канагав'':44,49,88 ''конкурс'':72,108,118 ''конкурсі'':6B,15C,38 ''країн'':84,117 ''культурн'':127 ''куточків'':131 ''маленьк'':28 ''малюнк'':8B,17C,40 ''малюнків'':114 ''марі'':30 ''матеріал'':150 ''мистецтв'':1A ''му'':36 ''міжнародн'':37 ''міської'':66 ''нагородж'':47 ''національної'':97 ''організатор'':86 ''організаці'':92 ''перемог'':33 ''перемогл'':4B,13C ''планет'':132 ''побут'':125 ''повідомляют'':62 ''пода'':110 ''понад'':82,111 ''префектур'':87 ''префектурі'':43 ''приз'':48 ''підтримці'':90 ''рад'':67 ''розвиток'':136 ''розмаїтт'':124 ''рок'':76 ''різних'':130 ''сайті'':64 ''світу'':85 ''сприяє'':119 ''став'':134 ''та'':77,140 ''творчої'':138 ''тисяч'':113 ''том'':94 ''традиці'':128 ''уяв'':139 ''уявлен'':121 ''федерації'':98 ''формуван'':120 ''художніх'':141 ''це'':61 ''цьогоріч'':106 ''черкаської'':65 ''черкащанк'':3B,12C,29 ''числі'':95 ''юн'':2B,11C ''юнеск'':100 ''японії'':10B,19C,45,101 ''єднує'':79 ''і'':96,126,133 ''із'':74,81,115 ''існує'':73',