INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (29, 'У Черкасах легковик підрізав мікроавтобус', 'f66a6d7c5a0676620c7863567f95aac1', 'У Черкасах легковик підрізав мікроавтобус', 'Дорожньо-транспортна пригода сталася на перехресті вулиць Симиренківської та Гетьмана Сагайдачного. Про це повідомляє пресслужба Патрульної поліції Черкаської області. Водій “Dacia Logan” здійснював розворот із середньої смуги та підрізав авто “Ford Transit”, що рухався в попутному напрямку в лівій смузі. Унаслідок зіткнення автомобілі отримали механічні пошкодження.', '<p class=\"bb_p\"><b class=\"bb_b\">Дорожньо-транспортна пригода сталася на перехресті вулиць Симиренківської та Гетьмана Сагайдачного.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-38-47_768.png" alt="" title="" /></p>
<p class=\"bb_p\">Про це повідомляє пресслужба Патрульної поліції Черкаської області.</p>
<p class=\"bb_p\">Водій “Dacia Logan” здійснював розворот із середньої смуги та підрізав авто “Ford Transit”, що рухався в попутному напрямку в лівій смузі.</p>
<p class=\"bb_p\">Унаслідок зіткнення автомобілі отримали механічні пошкодження.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://18000.com.ua/strichka-novin/u-cherkasax-legkovik-pidrizav-mikroavtobus-foto/" target="_blank">За матеріалами "18000.com.ua"</a></p></div>', 1, '2019-10-31 20:38:52.391119', '''18000.com.ua'':93 ''align'':36,89 ''b'':16,19 ''bb'':14,18,34,40,52,77,87 ''center'':37 ''class'':13,17,33,39,51,76,86 ''dacia'':55 ''ford'':65 ''logan'':56 ''p'':12,15,32,35,38,41,50,53,75,78,85,88 ''right'':90 ''transit'':66 ''авт'':64 ''автомобілі'':81 ''воді'':54 ''вулиц'':27 ''гетьма'':30 ''дорожн'':21 ''дорожньо-транспортн'':20 ''дтп'':1A ''здійснюва'':57 ''зіткнен'':80 ''легковик'':4B,9C ''лівій'':73 ''матеріал'':92 ''механічні'':83 ''мікроавтобус'':6B,11C ''напрямк'':71 ''області'':49 ''отрима'':82 ''патрульної'':46 ''перехресті'':26 ''повідомляє'':44 ''поліції'':47 ''попутн'':70 ''пошкоджен'':84 ''пресслужб'':45 ''пригод'':23 ''підрізав'':5B,10C,63 ''розворот'':58 ''рухав'':68 ''сагайдачн'':31 ''середньої'':60 ''симиренківської'':28 ''смуг'':61 ''смузі'':74 ''стал'':24 ''та'':29,62 ''транспортн'':22 ''унаслідок'':79 ''це'':43 ''черкас'':3B,8C ''черкаської'':48 ''що'':67 ''із'':59', 'ДТП', 1, 1, 0, 0, 1, 'https://18000.com.ua/strichka-novin/u-cherkasax-legkovik-pidrizav-mikroavtobus-foto/
Стрічка новин|ДТП|Черкаси');
INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (30, 'Нещасний випадок у Каневі: одна дитина померла, інша – в реанімації', '073f92ced2f5c832ca21fb22939597e9', 'Нещасний випадок у Каневі: одна дитина померла, інша – в реанімації', 'Трагічний інцидент стався сьогодні, 31 жовтня, у Каневі.  Інформацію %SITENAME% підтвердила речниця КУ “Обласний центр екстреної медичної допомоги та медицини катастроф ЧОР” Ірина Назарок.  Дівчинці було 15 років. Її та хлопчика-однолітка знайшли в автомобілі, що стояв у гаражі. У дівчинки медики констатували біологічну смерть. Хлопчика доправили в Канівську ЦРЛ до реанімаційного відділення у дуже тяжкому стані. За інформацією %SITENAME%, діти отруїлися чадним газом. Померла дівчинка була ученицею 10-А класу Канівської ЗОШ І-ІІІ ступенів №4.', '<p class=\"bb_p\"><b class=\"bb_b\">Трагічний інцидент стався сьогодні, 31 жовтня, у Каневі. </b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-38-52_866.png" alt="" title="" /></p>
<p class=\"bb_p\">Інформацію <b class=\"bb_b\">%SITENAME%</b> підтвердила речниця <b class=\"bb_b\">КУ “Обласний центр екстреної медичної допомоги та медицини катастроф ЧОР” Ірина Назарок. </b></p>
<p class=\"bb_p\">Дівчинці було 15 років. Її та хлопчика-однолітка знайшли в автомобілі, що стояв у гаражі.</p>
<p class=\"bb_p\">У дівчинки медики констатували біологічну смерть. Хлопчика доправили в Канівську ЦРЛ до реанімаційного відділення у дуже тяжкому стані.</p>
<p class=\"bb_p\">За інформацією <b class=\"bb_b\">%SITENAME%,</b> діти отруїлися чадним газом.</p>
<p class=\"bb_p\">Померла дівчинка була ученицею 10-А класу Канівської ЗОШ І-ІІІ ступенів №4.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://18000.com.ua/strichka-novin/neshhasnij-vipadok-u-kanevi-odna-ditina-pomerla-insha-v-reanimaci%d1%97/" target="_blank">За матеріалами "18000.com.ua"</a></p></div>', 1, '2019-10-31 20:38:54.569277', '''10'':137 ''15'':78 ''18000.com.ua'':155 ''31'':34 ''4'':146 ''align'':42,151 ''b'':26,29,49,52,56,59,120,123 ''bb'':24,28,40,46,51,58,74,94,116,122,131,149 ''center'':43 ''class'':23,27,39,45,50,57,73,93,115,121,130,148 ''p'':22,25,38,41,44,47,72,75,92,95,114,117,129,132,147,150 ''right'':152 ''sitenam'':53,124 ''автомобілі'':87 ''бул'':77,135 ''біологічн'':100 ''випадок'':3B,13C ''відділен'':109 ''газ'':128 ''гаражі'':91 ''дитин'':7B,17C ''допомог'':65 ''доправ'':103 ''дуж'':111 ''дівчинк'':97,134 ''дівчинці'':76 ''діти'':125 ''екстреної'':63 ''жовтн'':35 ''знайшл'':85 ''зош'':141 ''каневі'':5B,15C,37 ''канів'':1A ''канівськ'':105 ''канівської'':140 ''катастроф'':68 ''клас'':139 ''констатува'':99 ''ку'':60 ''матеріал'':154 ''медик'':98 ''медицин'':67 ''медичної'':64 ''назарок'':71 ''нещасн'':2B,12C ''обласн'':61 ''одн'':6B,16C ''однолітк'':84 ''отруїл'':126 ''померл'':8B,18C,133 ''підтверд'':54 ''реанімаційн'':108 ''реанімації'':11B,21C ''речниц'':55 ''років'':79 ''смерт'':101 ''став'':32 ''стані'':113 ''стоя'':89 ''ступенів'':145 ''сьогодні'':33 ''та'':66,81 ''трагічн'':30 ''тяжк'':112 ''учениц'':136 ''хлопчик'':83,102 ''хлопчика-однолітк'':82 ''центр'':62 ''црл'':106 ''чадн'':127 ''чор'':69 ''що'':88 ''і'':143 ''і-ііі'':142 ''інформаці'':48 ''інформаціє'':119 ''інцидент'':31 ''інша'':9B,19C ''ірин'':70 ''ііі'':144 ''її'':80', 'Канів', 1, 1, 0, 0, 1, 'https://18000.com.ua/strichka-novin/neshhasnij-vipadok-u-kanevi-odna-ditina-pomerla-insha-v-reanimaci%d1%97/
Стрічка новин|Супер слайдер|Канів');
INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (37, 'У Шполі стерилізували 43 собаки', 'c92f075b6776a1273934454f76b33878', 'У Шполі стерилізували 43 собаки', '       Сьогодні у Шполі проводять стерилізацію та вакцинацію тварин-безхатченків. Тема безпритульних собак не втрачає актуальності у нашому місті. Тому задля попередження подальшого  розмноження  бродячих тварин місцева влада застосовує гуманні методи.  Наразі працівниками КП «Благоустрій» відловлено 43 собаки, які успішно простерилізовані  фаховим ветлікарем, а також проведено, всім без виключення, вакцинацію від  сказу та чіпування.         Кошти на відповідні процедури виділено із міського бюджету згідно Програми регулювання чисельності безпритульних тварин гуманними методами в Шполянській міській об’єднаній територіальній громаді на 2018-2020 роки.', '<p class=\"bb_p\"><b class=\"bb_b\">       </b><b class=\"bb_b\">Сьогодні у Шполі проводять стерилізацію та вакцинацію тварин-безхатченків. Тема безпритульних собак не втрачає актуальності у нашому місті. Тому задля попередження подальшого  розмноження  бродячих тварин місцева влада застосовує гуманні методи.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-39-21_804.png" alt="" title="" /></p>
<p class=\"bb_p\"> Наразі працівниками КП «Благоустрій» відловлено 43 собаки, які успішно простерилізовані  фаховим ветлікарем, а також проведено, всім без виключення, вакцинацію від  сказу та чіпування.</p>
<p class=\"bb_p\">        Кошти на відповідні процедури виділено із міського бюджету згідно Програми регулювання чисельності безпритульних тварин гуманними методами в Шполянській міській об’єднаній територіальній громаді на 2018-2020 роки.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://shpola-otg.gov.ua/2019/10/29/u-shpoli-sterylizuvaly-43-sobaky/" target="_blank">За матеріалами "shpola-otg.gov.ua"</a></p></div>', 1, '2019-10-31 20:39:23.724577', '''-2020'':116 ''2018'':115 ''43'':4B,9C,69 ''align'':58,122 ''b'':15,18,19,22 ''bb'':13,17,21,56,62,89,120 ''center'':59 ''class'':12,16,20,55,61,88,119 ''p'':11,14,54,57,60,63,87,90,118,121 ''right'':123 ''shpola-otg.gov.ua'':126 ''актуальності'':38 ''безпритульн'':34,103 ''безхатченків'':32 ''благоустрі'':67 ''бродяч'':47 ''бюджет'':98 ''вакцинаці'':29,82 ''ветлікар'':75 ''виділ'':95 ''виключен'':81 ''влад'':50 ''всім'':79 ''втрачає'':37 ''від'':83 ''відловл'':68 ''відповідні'':93 ''громаді'':113 ''гума'':105 ''гуманні'':52 ''задл'':43 ''застосовує'':51 ''згідно'':99 ''кошт'':91 ''кп'':66 ''матеріал'':125 ''метод'':53,106 ''місті'':41 ''місцев'':49 ''міськог'':97 ''міській'':109 ''наразі'':64 ''наш'':40 ''подальш'':45 ''попереджен'':44 ''працівник'':65 ''провед'':78 ''проводя'':26 ''прогр'':100 ''простерилізовані'':73 ''процедур'':94 ''регулюван'':101 ''розмножен'':46 ''рок'':117 ''сказ'':84 ''собак'':5B,10C,35,70 ''стерилізаці'':27 ''стерилізува'':3B,8C ''сьогодні'':23 ''та'':28,85 ''також'':77 ''тварин'':31,48,104 ''тварин-безхатченків'':30 ''тем'':33 ''територіальні'':112 ''том'':42 ''успішн'':72 ''фахов'':74 ''чисельності'':102 ''чіпуван'':86 ''шполянські'':108 ''шполі'':2B,7C,25 ''які'':71 ''єднані'':111 ''із'':96', '', 1, 1, 0, 0, 1, 'http://shpola-otg.gov.ua/2019/10/29/u-shpoli-sterylizuvaly-43-sobaky/
жкг');
INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (38, 'Творити добро легко і приємно', '6fa2e73a8971a0d64da2c00abb4f634c', 'Творити добро легко і приємно', '       Цього року у міському парку дитячого дозвілля проходила благодійна акція «Допоможи покращити життя діткам!».  Протягом вересня працівники парку  збирали дитячі іграшки для діток із малозабезпечених родин. [img][/img] Наразі адміністрацією дитячого парку речі передано 66 дітям із 27 потребуючих сімей. Родини із вдячністю приймали допомогу, а очі малят світились щасливими і грайливими вогниками. Метою акції було заохочення громадськості до благодійності, вчитись проявляти доброту і любов людям, які найбільше цього чекають.  Дякуємо всім громадянам, хто долучився до акції «Допоможи покращити життя діткам!» за проявлену турботу.  Добро повернеться сторицею   вам і вашим родинам!!!', '<p class=\"bb_p\"><b class=\"bb_b\">       Цього року у міському парку дитячого дозвілля проходила благодійна акція «Допоможи покращити життя діткам!».  Протягом вересня працівники парку  збирали дитячі іграшки для діток із малозабезпечених родин.</b></p>
<p class=\"bb_p align_center\">[img][/img]</p>
<p class=\"bb_p\">Наразі адміністрацією дитячого парку речі передано 66 дітям із 27 потребуючих сімей. Родини із вдячністю приймали допомогу, а очі малят світились щасливими і грайливими вогниками.</p>
<p class=\"bb_p\">Метою акції було заохочення громадськості до благодійності, вчитись проявляти доброту і любов людям, які найбільше цього чекають.</p>
<p class=\"bb_p\"><b class=\"bb_b\"> </b><b class=\"bb_b\">Дякуємо всім громадянам, хто долучився до акції «Допоможи покращити життя діткам!» за проявлену турботу.  Добро повернеться сторицею   вам і вашим родинам!!!</b></p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://shpola-otg.gov.ua/2019/10/29/tvoryty-dobro-lehko-i-pryiemno/" target="_blank">За матеріалами "shpola-otg.gov.ua"</a></p></div>', 1, '2019-10-31 20:39:26.817405', '''/img'':52 ''27'':66 ''66'':63 ''align'':49,140 ''b'':15,18,107,110,111,114 ''bb'':13,17,47,55,84,105,109,113,138 ''center'':50 ''class'':12,16,46,54,83,104,108,112,137 ''img'':51 ''p'':11,14,45,48,53,56,82,85,103,106,136,139 ''right'':141 ''shpola-otg.gov.ua'':144 ''адміністраціє'':58 ''акці'':28 ''акції'':87,121 ''благодійн'':27 ''благодійності'':92 ''бул'':88 ''ваш'':134 ''вдячніст'':71 ''вересн'':34 ''вогник'':81 ''всім'':116 ''вчит'':93 ''грайлив'':80 ''громадськості'':90 ''громадян'':117 ''дитяч'':24,59 ''дитячі'':38 ''добр'':2B,7C,129 ''доброт'':95 ''дозвілл'':25 ''долучив'':119 ''допомог'':73 ''допомож'':29,122 ''дякуєм'':115 ''діткам'':32,125 ''діток'':41 ''дітям'':64 ''житт'':31,124 ''заохочен'':89 ''збира'':37 ''легк'':3B,8C ''люб'':97 ''люд'':98 ''мал'':76 ''малозабезпечен'':43 ''матеріал'':143 ''мет'':86 ''міськом'':22 ''найбільш'':100 ''наразі'':57 ''очі'':75 ''парк'':23,36,60 ''переда'':62 ''повернет'':130 ''покращит'':30,123 ''потребуюч'':67 ''працівник'':35 ''прийма'':72 ''приємн'':5B,10C ''протяг'':33 ''проход'':26 ''проявлен'':127 ''проявлят'':94 ''речі'':61 ''родин'':44,69,135 ''рок'':20 ''світил'':77 ''сториц'':131 ''сіме'':68 ''творит'':1B,6C ''турбот'':128 ''хто'':118 ''цьог'':19,101 ''чекают'':102 ''щаслив'':78 ''які'':99 ''і'':4B,9C,79,96,133 ''іграшк'':39 ''із'':42,65,70', '', 1, 1, 0, 0, 0, 'http://shpola-otg.gov.ua/2019/10/29/tvoryty-dobro-lehko-i-pryiemno/
