INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (39, 'Творити добро легко і приємно', '6fa2e73a8971a0d64da2c00abb4f634c', 'Творити добро легко і приємно', '       Цього року у міському парку дитячого дозвілля проходила благодійна акція «Допоможи покращити життя діткам!».  Протягом вересня працівники парку  збирали дитячі іграшки для діток із малозабезпечених родин. [img][/img] Наразі адміністрацією дитячого парку речі передано 66 дітям із 27 потребуючих сімей. Родини із вдячністю приймали допомогу, а очі малят світились щасливими і грайливими вогниками. Метою акції було заохочення громадськості до благодійності, вчитись проявляти доброту і любов людям, які найбільше цього чекають.  Дякуємо всім громадянам, хто долучився до акції «Допоможи покращити життя діткам!» за проявлену турботу.  Добро повернеться сторицею   вам і вашим родинам!!!', '<p class=\"bb_p\"><b class=\"bb_b\">       Цього року у міському парку дитячого дозвілля проходила благодійна акція «Допоможи покращити життя діткам!».  Протягом вересня працівники парку  збирали дитячі іграшки для діток із малозабезпечених родин.</b></p>
<p class=\"bb_p align_center\">[img][/img]</p>
<p class=\"bb_p\">Наразі адміністрацією дитячого парку речі передано 66 дітям із 27 потребуючих сімей. Родини із вдячністю приймали допомогу, а очі малят світились щасливими і грайливими вогниками.</p>
<p class=\"bb_p\">Метою акції було заохочення громадськості до благодійності, вчитись проявляти доброту і любов людям, які найбільше цього чекають.</p>
<p class=\"bb_p\"><b class=\"bb_b\"> </b><b class=\"bb_b\">Дякуємо всім громадянам, хто долучився до акції «Допоможи покращити життя діткам!» за проявлену турботу.  Добро повернеться сторицею   вам і вашим родинам!!!</b></p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://shpola-otg.gov.ua/2019/10/29/tvoryty-dobro-lehko-i-pryiemno/" target="_blank">За матеріалами "shpola-otg.gov.ua"</a></p></div>', 1, '2019-10-31 20:39:29.116529', '''/img'':52 ''27'':66 ''66'':63 ''align'':49,140 ''b'':15,18,107,110,111,114 ''bb'':13,17,47,55,84,105,109,113,138 ''center'':50 ''class'':12,16,46,54,83,104,108,112,137 ''img'':51 ''p'':11,14,45,48,53,56,82,85,103,106,136,139 ''right'':141 ''shpola-otg.gov.ua'':144 ''адміністраціє'':58 ''акці'':28 ''акції'':87,121 ''благодійн'':27 ''благодійності'':92 ''бул'':88 ''ваш'':134 ''вдячніст'':71 ''вересн'':34 ''вогник'':81 ''всім'':116 ''вчит'':93 ''грайлив'':80 ''громадськості'':90 ''громадян'':117 ''дитяч'':24,59 ''дитячі'':38 ''добр'':2B,7C,129 ''доброт'':95 ''дозвілл'':25 ''долучив'':119 ''допомог'':73 ''допомож'':29,122 ''дякуєм'':115 ''діткам'':32,125 ''діток'':41 ''дітям'':64 ''житт'':31,124 ''заохочен'':89 ''збира'':37 ''легк'':3B,8C ''люб'':97 ''люд'':98 ''мал'':76 ''малозабезпечен'':43 ''матеріал'':143 ''мет'':86 ''міськом'':22 ''найбільш'':100 ''наразі'':57 ''очі'':75 ''парк'':23,36,60 ''переда'':62 ''повернет'':130 ''покращит'':30,123 ''потребуюч'':67 ''працівник'':35 ''прийма'':72 ''приємн'':5B,10C ''протяг'':33 ''проход'':26 ''проявлен'':127 ''проявлят'':94 ''речі'':61 ''родин'':44,69,135 ''рок'':20 ''світил'':77 ''сториц'':131 ''сіме'':68 ''творит'':1B,6C ''турбот'':128 ''хто'':118 ''цьог'':19,101 ''чекают'':102 ''щаслив'':78 ''які'':99 ''і'':4B,9C,79,96,133 ''іграшк'':39 ''із'':42,65,70', '', 1, 1, 0, 0, 0, 'http://shpola-otg.gov.ua/2019/10/29/tvoryty-dobro-lehko-i-pryiemno/
