INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (34, 'Розумна доброта — це мудрості роки, які приходять з часом', 'bd8fcbbe46525b85e11bfcafd15f7f01', 'Розумна доброта — це мудрості роки, які приходять з часом', '      Ми завжди беремо приклад у найстарших, навчаємось  секрету довголіття,  який вони вбачають в оптимізмі, доброті, чистоті сердець, любові та повазі одне до одного. Тому місцева влада  ніколи не забуває про наймудріших жителів об’єднаної громади у їх дні народження. У жовтні представники керівного складу міської ОТГ привітали одинадцять мудрих довгожителів. Вісім іменинників із міста Шполи, двоє із с. Лебедин та одну жительку с. Капустине. Найщиріші слова вдячності, подарунки, квіти та побажання міцного здоров’я найстаршим шполянам висловили — перший заступник міського голови Олександр Кузуб, депутати міської ради ОТГ — Марія Тищенко, Олег Кошовий, Юрій Голуб,  працівники Територіального центру соціального обслуговування, у селах ювілярів вітали старости та соцпрацівники. Протягом жовтня у м. Шпола  90 та понад 90-річний рубіж святкували — Зінаїда Василівна Курінна (90 років), Лідія Карпівна Кобишева (92 роки), Євдокія Яківна Царинник (92 роки), Любов Олексіївна Плосконос (92 роки), Любов Ільківна Стукало (91 рік), Володимир Станіславович Брянський (92 роки), Антоніна Федорівна Сухар (90 років), Віра Степанівна Чернецька (91 рік). У Лебедині — Катерина Ілларіонівна Гребінюченко (90 років) та Лідія Іванівна Зозуля (91 рік). У Капустине —Галина Василівна Бойко (92 роки).         Висловлюємо їм слова подяки за багаторічну самовіддану працю, життєву мудрість, багатий досвід, за пережиті бурхливі події воєнного лихоліття та труднощі післявоєнної відбудови. Нехай їхнє життя буде сповнене увагою і теплом   рідних, здоров’ям, миром та спокоєм.', '<p class=\"bb_p\"><b class=\"bb_b\">      </b><b class=\"bb_b\">Ми завжди беремо приклад у найстарших, навчаємось  секрету довголіття,  який вони вбачають в оптимізмі, доброті, чистоті сердець, любові та повазі одне до одного. Тому місцева влада  ніколи не забуває про наймудріших жителів об’єднаної громади у їх дні народження.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-39-10_297.png" alt="" title="" /></p>
<p class=\"bb_p\">У жовтні представники керівного складу міської ОТГ привітали одинадцять мудрих довгожителів. Вісім іменинників із міста Шполи, двоє із с. Лебедин та одну жительку с. Капустине.</p>
<p class=\"bb_p\">Найщиріші слова вдячності, подарунки, квіти та побажання міцного здоров’я найстаршим шполянам висловили — перший заступник міського голови Олександр Кузуб, депутати міської ради ОТГ — Марія Тищенко, Олег Кошовий, Юрій Голуб,  працівники Територіального центру соціального обслуговування, у селах ювілярів вітали старости та соцпрацівники.</p>
<p class=\"bb_p\"><b class=\"bb_b\">Протягом жовтня у м. Шпола</b>  90 та понад 90-річний рубіж святкували — Зінаїда Василівна Курінна (90 років), Лідія Карпівна Кобишева (92 роки), Євдокія Яківна Царинник (92 роки), Любов Олексіївна Плосконос (92 роки), Любов Ільківна Стукало (91 рік), Володимир Станіславович Брянський (92 роки), Антоніна Федорівна Сухар (90 років), Віра Степанівна Чернецька (91 рік).</p>
<p class=\"bb_p\"><b class=\"bb_b\">У Лебедині</b> — Катерина Ілларіонівна Гребінюченко (90 років) та Лідія Іванівна Зозуля (91 рік).</p>
<p class=\"bb_p\"><b class=\"bb_b\">У Капустине —</b>Галина Василівна Бойко (92 роки).</p>
<p class=\"bb_p\"><b class=\"bb_b\">        Висловлюємо їм слова подяки за багаторічну самовіддану працю, життєву мудрість, багатий досвід, за пережиті бурхливі події воєнного лихоліття та труднощі післявоєнної відбудови. Нехай їхнє життя буде сповнене увагою і теплом   рідних, здоров’ям, миром та спокоєм.</b></p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://shpola-otg.gov.ua/2019/10/31/rozumna-dobrota-tse-mudrosti-roky-iaki-prykhodyt-z-chasom/" target="_blank">За матеріалами "shpola-otg.gov.ua"</a></p></div>', 1, '2019-10-31 20:39:17.014512', '''90'':163,166,173,203,223 ''91'':193,208,229 ''92'':178,183,188,198,244 ''align'':74,294 ''b'':23,26,27,30,154,157,214,217,235,238,250,253 ''bb'':21,25,29,72,78,107,152,156,212,216,233,237,248,252,292 ''center'':75 ''class'':20,24,28,71,77,106,151,155,211,215,232,236,247,251,291 ''p'':19,22,70,73,76,79,105,108,150,153,210,213,231,234,246,249,290,293 ''right'':295 ''shpola-otg.gov.ua'':298 ''антонін'':200 ''багат'':264 ''багаторічн'':259 ''берем'':33 ''бойк'':243 ''брянськ'':197 ''буд'':279 ''бурхливі'':268 ''василівн'':171,242 ''вбачают'':42 ''вдячності'':111 ''вислов'':121 ''висловлюєм'':254 ''влад'':56 ''володимир'':195 ''вон'':41 ''воєн'':270 ''відбудов'':275 ''віра'':205 ''вісім'':91 ''вітал'':146 ''галин'':241 ''голов'':125 ''голуб'':137 ''гребінюченк'':222 ''громад'':65 ''двоє'':96 ''депутат'':128 ''дні'':68 ''доброт'':2B,11C ''доброті'':45 ''довгожителів'':90 ''довголітт'':39 ''досвід'':265 ''жительк'':102 ''жителів'':62 ''житт'':278 ''життєв'':262 ''жовтн'':159 ''жовтні'':81 ''з'':8B,17C ''забуває'':59 ''завжд'':32 ''заступник'':123 ''здор'':117,285 ''зозул'':228 ''зінаїд'':170 ''капустин'':104,240 ''карпівн'':176 ''катерин'':220 ''квіти'':113 ''керівн'':83 ''кобишев'':177 ''кошов'':135 ''кузуб'':127 ''курін'':172 ''лебедин'':99 ''лебедині'':219 ''лихолітт'':271 ''люб'':185,190 ''любові'':48 ''лідія'':175,226 ''м'':161 ''марі'':132 ''матеріал'':297 ''ми'':31 ''мир'':287 ''мудр'':89 ''мудрості'':4B,13C ''мудріст'':263 ''міста'':94 ''місцев'':55 ''міськог'':124 ''міської'':85,129 ''міцног'':116 ''навчаєм'':37 ''наймудріш'':61 ''найстарш'':36,119 ''найщиріші'':109 ''народжен'':69 ''неха'':276 ''нікол'':57 ''обслуговуван'':142 ''одинадця'':88 ''одн'':51,53,101 ''олег'':134 ''олександр'':126 ''олексіївн'':186 ''оптимізмі'':44 ''отг'':86,131 ''пережиті'':267 ''перш'':122 ''плосконос'':187 ''побажан'':115 ''повазі'':50 ''подарунк'':112 ''подяк'':257 ''події'':269 ''понад'':165 ''прац'':261 ''працівник'':138 ''представник'':82 ''привіта'':87 ''приклад'':34 ''приходя'':7B,16C ''протяг'':158 ''післявоєнної'':274 ''рад'':130 ''розумн'':1B,10C ''рок'':5B,14C,179,184,189,199,245 ''років'':174,204,224 ''рубіж'':168 ''рідних'':284 ''рік'':194,209,230 ''річни'':167 ''самовіддан'':260 ''святкува'':169 ''секрет'':38 ''сел'':144 ''сердец'':47 ''склад'':84 ''слов'':110,256 ''соцпрацівник'':149 ''соціальн'':141 ''сповнен'':280 ''спокоєм'':289 ''станіславович'':196 ''старост'':147 ''степанівн'':206 ''стука'':192 ''сухар'':202 ''та'':49,100,114,148,164,225,272,288 ''тепл'':283 ''територіальн'':139 ''тищенк'':133 ''том'':54 ''труднощі'':273 ''уваг'':281 ''федорівн'':201 ''царинник'':182 ''це'':3B,12C ''центр'':140 ''час'':9B,18C ''чернецьк'':207 ''чистоті'':46 ''шпол'':95,162 ''шполян'':120 ''ювілярів'':145 ''юрі'':136 ''як'':40 ''які'':6B,15C ''яківн'':181 ''ям'':286 ''євдокі'':180 ''єднаної'':64 ''і'':282 ''іванівн'':227 ''із'':93,97 ''ілларіонівн'':221 ''ільківна'':191 ''іменинників'':92 ''їм'':255 ''їх'':67 ''їхнє'':277', '', 1, 1, 0, 0, 1, 'http://shpola-otg.gov.ua/2019/10/31/rozumna-dobrota-tse-mudrosti-roky-iaki-prykhodyt-z-chasom/
соціальний захист');
INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (35, 'Засідання виконавчого комітету', '700b043b07cce4df1713ed836c644a47', 'Засідання виконавчого комітету', 'Вчора на вечірньому засіданні членами виконкому Шполянської міської ОТГ були прийняті важливі рішення. Відповідно до порядку денного розглянули 26 питань, значну частину з яких задовольнили. Зокрема,  рішення, які  стосуються соціальної підтримки потребуючих жителів громади були підтримані одноголосно. [img][/img] Цього разу члени виконкому погодили виділення одноразової матеріальної допомоги учасникам ООС/АТО, онкохворим, особам із тяжкими, середніми і хронічними захворюваннями на загальну суму 29 тис. 600 грн, з них — учасникам бойових дій по 1400 грн (кожному), онкохворим по 1900 грн, тяжким хворим по 600 грн тощо. Затвердили надання пільг на оплату за харчування дітей у закладах дошкільної освіти міста, пільг з оплати за навчання у Шполянській дитячій школі мистецтв (багатодітним, малозабезпеченим сім’ям, дітям-інвалідам,  дітям, які навчаються на декількох фахових відділеннях, обдарованим дітям).          Із бюджетних рішень — члени виконкому прийняли надходження до бюджету громади державної субвенції (державна підтримка особам із особливими освітніми потребами) загальною сумою 26 тис. 886 грн. Ці кошти підуть на придбання спеціальних засобів корекції психофізичного розвитку дітей з особливими освітніми потребами, які навчаються в інклюзивних класах закладів загальної середньої освіти та оплату праці педагогам, що навчають особливих дітей. Розглянули та винесли відповідні рішення подані органом опіки і піклування стосовно прав захисту дітей,  питання містобудівного та архітектурного характеру, житлово-комунального господарства, а також ухвалили рішення щодо взяття на квартирний облік дітей-сиріт, дітей позбавлених батьківського піклування, осіб із їх числа міської ОТГ.  ', '<p class=\"bb_p\"><b class=\"bb_b\">Вчора на вечірньому засіданні членами виконкому Шполянської міської ОТГ були прийняті важливі рішення. Відповідно до порядку денного розглянули 26 питань, значну частину з яких задовольнили. Зокрема,  рішення, які  стосуються соціальної підтримки потребуючих жителів громади були підтримані одноголосно.</b></p>
<p class=\"bb_p align_center\">[img][/img]</p>
<p class=\"bb_p\">Цього разу члени виконкому погодили виділення одноразової матеріальної допомоги учасникам ООС/АТО, онкохворим, особам із тяжкими, середніми і хронічними захворюваннями на загальну суму 29 тис. 600 грн, з них — учасникам бойових дій по 1400 грн (кожному), онкохворим по 1900 грн, тяжким хворим по 600 грн тощо. Затвердили надання пільг на оплату за харчування дітей у закладах дошкільної освіти міста, пільг з оплати за навчання у Шполянській дитячій школі мистецтв (багатодітним, малозабезпеченим сім’ям, дітям-інвалідам,  дітям, які навчаються на декількох фахових відділеннях, обдарованим дітям).</p>
<p class=\"bb_p\"><b class=\"bb_b\">         Із бюджетних рішень</b> — члени виконкому прийняли надходження до бюджету громади державної субвенції (державна підтримка особам із особливими освітніми потребами) загальною сумою 26 тис. 886 грн. Ці кошти підуть на придбання спеціальних засобів корекції психофізичного розвитку дітей з особливими освітніми потребами, які навчаються в інклюзивних класах закладів загальної середньої освіти та оплату праці педагогам, що навчають особливих дітей.</p>
<p class=\"bb_p\">Розглянули та винесли відповідні рішення подані органом опіки і піклування стосовно прав захисту дітей,  питання містобудівного та архітектурного характеру, житлово-комунального господарства, а також ухвалили рішення щодо взяття на квартирний облік дітей-сиріт, дітей позбавлених батьківського піклування, осіб із їх числа міської ОТГ.</p>
