INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (53, 'В Умані зростуть тарифи на ритуальні послуги', 'a7428bf39d84680da1f29c3e84676651', 'В Умані зростуть тарифи на ритуальні послуги', 'У місті Умань планують підняти тарифи на ритуальні послуги, які надає Уманське комунальне підприємство «Комунальник». Про це йшлося 16 жовтня під час засідання робочої групи з перевірки тарифів на житлово-комунальні, ритуальні послуги та послуги з перевезення пасажирів у міському транспорті. Як зазначається на сайті виконавчого комітету Уманської міської ради, засідання робочої групи проходило під головуванням заступника голови робочої групи Бовгири С. І.', '<p class=\"bb_p\"><b class=\"bb_b\">У місті Умань планують підняти тарифи на ритуальні послуги, які надає Уманське комунальне підприємство «Комунальник».</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-40-04_246.png" alt="" title="" /></p>
<p class=\"bb_p\">Про це йшлося 16 жовтня під час засідання робочої групи з перевірки тарифів на житлово-комунальні, ритуальні послуги та послуги з перевезення пасажирів у міському транспорті.</p>
<p class=\"bb_p\">Як зазначається <b class=\"bb_b\">на сайті</b> виконавчого комітету Уманської міської ради, засідання робочої групи проходило під головуванням заступника голови робочої групи Бовгири С. І.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://vch-uman.in.ua/v-umani-zrostut-tarifi-na-ritualni-p/" target="_blank">За матеріалами "vch-uman.in.ua"</a></p></div>', 1, '2019-10-31 20:40:08.078519', '''16'':51 ''align'':42,109 ''b'':19,22,81,84 ''bb'':17,21,40,46,77,83,107 ''center'':43 ''class'':16,20,39,45,76,82,106 ''p'':15,18,38,41,44,47,75,78,105,108 ''right'':110 ''vch-uman.in.ua'':113 ''бовгир'':102 ''виконавч'':87 ''голов'':99 ''головуван'':97 ''груп'':57,94,101 ''житлов'':63 ''житлово-комунальні'':62 ''жовтн'':52 ''з'':58,69 ''зазначаєт'':80 ''заступник'':98 ''засідан'':55,92 ''зростут'':3B,10C ''йшло'':50 ''комунальн'':35 ''комунальник'':37 ''комунальні'':64 ''комітет'':88 ''матеріал'':112 ''місті'':24 ''міськом'':73 ''міської'':90 ''надає'':33 ''пасажирів'':71 ''перевезен'':70 ''перевірк'':59 ''плануют'':26 ''послуг'':7B,14C,31,66,68 ''проход'':95 ''під'':53,96 ''піднят'':27 ''підприємств'':36 ''рад'':91 ''ритуальні'':6B,13C,30,65 ''робочої'':56,93,100 ''сайті'':86 ''та'':67 ''тариф'':4B,11C,28 ''тарифів'':60 ''транспорті'':74 ''уман'':25 ''уманськ'':34 ''уманської'':89 ''умані'':2B,9C ''це'':49 ''час'':54 ''як'':79 ''які'':32 ''і'':104', '', 1, 1, 0, 0, 1, 'https://vch-uman.in.ua/v-umani-zrostut-tarifi-na-ritualni-p/
