INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (52, 'Юні уманчани здобули ІІІ місце на Всеукраїнському фестивалі «Козацький гарт»', '18ed4ca65ee11f68c8e912f48fb3831f', 'Юні уманчани здобули ІІІ місце на Всеукраїнському фестивалі «Козацький гарт»', 'З 7 по 11 жовтня в місті Галич Івано-Франківської області проходили фінальні змагання Всеукраїнського етапу фізкультурно-патріотичного фестивалю школярів «Козацький гарт 2019». У змаганнях брали участь команди з десяти областей України. Черкаську область представляла команда юних козаків Уманської загальноосвітньої школи І-ІІІ ступенів №3 під керівництвом вчителя фізичної культури Олексія Шеленкова. Як повідомили у виконавчому комітеті Уманської міської ради, за результатами турніру уманчани посіли ІІІ місце. Склад команди юних козаків Уманської ЗОШ №3: Карина Іванюк, учениця 5 класу;Олександр Заводянський, учень 5 класу;Марія Поліщук, учениця 6 класу;Михайло Овчарук, учень 6 класу;Юлія Березницька, учениця 7 класу;Михайло Пестерев, учень 7 класу;Богдан Мельник, учень 8 класу;Анна Зозуля, учениця 8 класу;Софія Коваленко, учениця 9 класу;Дмитро Гріднєв, учень 9 класу;Анастасія Дячок, учениця 10 класу;Олександр Осичка, учень 10 класу.', '<p class=\"bb_p\"><b class=\"bb_b\">З 7 по 11 жовтня в місті Галич Івано-Франківської області проходили фінальні змагання Всеукраїнського етапу фізкультурно-патріотичного фестивалю школярів «Козацький гарт 2019». У змаганнях брали участь команди з десяти областей України.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-40-00_193.png" alt="" title="" /></p>
<p class=\"bb_p\">Черкаську область представляла команда юних козаків Уманської загальноосвітньої школи І-ІІІ ступенів №3 під керівництвом вчителя фізичної культури Олексія Шеленкова.</p>
<p class=\"bb_p\">Як <b class=\"bb_b\">повідомили</b> у виконавчому комітеті Уманської міської ради, за результатами турніру уманчани посіли ІІІ місце.</p>
<p class=\"bb_p\">Склад команди юних козаків Уманської ЗОШ №3:</p>
<p class=\"bb_p\">Карина Іванюк, учениця 5 класу;Олександр Заводянський, учень 5 класу;Марія Поліщук, учениця 6 класу;Михайло Овчарук, учень 6 класу;Юлія Березницька, учениця 7 класу;Михайло Пестерев, учень 7 класу;Богдан Мельник, учень 8 класу;Анна Зозуля, учениця 8 класу;Софія Коваленко, учениця 9 класу;Дмитро Гріднєв, учень 9 класу;Анастасія Дячок, учениця 10 класу;Олександр Осичка, учень 10 класу.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://vch-uman.in.ua/juni-umanchani-zdobuli-iii-misce-na-vseu/" target="_blank">За матеріалами "vch-uman.in.ua"</a></p></div>', 1, '2019-10-31 20:40:05.555376', '''10'':185,190 ''11'':32 ''2019'':53 ''3'':86,127 ''5'':135,140 ''6'':145,150 ''7'':30,155,160 ''8'':165,170 ''9'':175,180 ''align'':67,196 ''b'':25,28,99,102 ''bb'':23,27,65,71,96,101,119,130,194 ''center'':68 ''class'':22,26,64,70,95,100,118,129,193 ''p'':21,24,63,66,69,72,94,97,117,120,128,131,192,195 ''right'':197 ''vch-uman.in.ua'':200 ''ан'':167 ''анастасі'':182 ''березницьк'':153 ''богда'':162 ''брал'':56 ''виконавч'':105 ''всеукраїнськ'':7B,17C,44 ''вчител'':89 ''галич'':36 ''гарт'':10B,20C,52 ''гріднєв'':178 ''десят'':60 ''дмитр'':177 ''дячок'':183 ''етап'':45 ''жовтн'':33 ''з'':29,59 ''заводянськ'':138 ''загальноосвітньої'':80 ''здобул'':3B,13C ''змаган'':43,55 ''зозул'':168 ''зош'':126 ''карин'':132 ''керівництв'':88 ''клас'':136,141,146,151,156,161,166,171,176,181,186,191 ''коваленк'':173 ''козаків'':78,124 ''козацьк'':9B,19C,51 ''команд'':58,76,122 ''комітеті'':106 ''культур'':91 ''марі'':142 ''матеріал'':199 ''мельник'':163 ''михайл'':147,157 ''місті'':35 ''місце'':5B,15C,116 ''міської'':108 ''област'':61,74 ''області'':40 ''овчарук'':148 ''олександр'':137,187 ''олексі'':92 ''осичк'':188 ''патріотичн'':48 ''пестер'':158 ''повідом'':103 ''поліщук'':143 ''посіл'':114 ''представля'':75 ''проход'':41 ''під'':87 ''рад'':109 ''результат'':111 ''склад'':121 ''софі'':172 ''ступенів'':85 ''турнір'':112 ''україн'':62 ''уманської'':79,107,125 ''уманчан'':2B,12C,113 ''участ'':57 ''учен'':139,149,159,164,179,189 ''учениц'':134,144,154,169,174,184 ''фестивал'':49 ''фестивалі'':8B,18C ''франківської'':39 ''фізичної'':90 ''фізкультурн'':47 ''фізкультурно-патріотичн'':46 ''фінальні'':42 ''черкаськ'':73 ''шеленков'':93 ''школ'':81 ''школярів'':50 ''юлі'':152 ''юн'':77,123 ''юні'':1B,11C ''як'':98 ''і'':83 ''і-ііі'':82 ''іван'':38 ''івано-франківської'':37 ''іванюк'':133 ''ііі'':4B,14C,84,115', '', 1, 1, 0, 0, 1, 'https://vch-uman.in.ua/juni-umanchani-zdobuli-iii-misce-na-vseu/
