INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (57, 'В Умані відбудеться концерт гітарної музики', 'ecf259d3354ace8711da7dda20c13f99', 'В Умані відбудеться концерт гітарної музики', '25 жовтня у концертній залі Уманської дитячої музичної школи №1 відбудеться концерт гітарної музики у виконанні студентів Уманського обласного музичного училища імені Порфирія Демуцького. Початок о 15:00. Вхід вільний. Про це йдеться на сторінці Уманського обласного музичного училища імені Порфирія Демуцького у соцмережі Facebook. На сцені виступатимуть студенти музичного училища: Світлана Біліченко, Артем Сторчак, Максим Дорошенко, Богдан Чеботарьов, Євген Богдановичта Віктор Бондаренко. Усі виконавці навчаються у класі викладача Анатолія Коваленка.', '<p class=\"bb_p\"><b class=\"bb_b\">25 жовтня у концертній залі Уманської дитячої музичної школи №1 відбудеться концерт гітарної музики у виконанні студентів Уманського обласного музичного училища імені Порфирія Демуцького. Початок о 15:00. Вхід вільний.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-40-09_427.png" alt="" title="" /></p>
<p class=\"bb_p\">Про це йдеться <b class=\"bb_b\">на сторінці</b> Уманського обласного музичного училища імені Порфирія Демуцького у соцмережі Facebook.</p>
<p class=\"bb_p\">На сцені виступатимуть студенти музичного училища: <b class=\"bb_b\">Світлана Біліченко</b>, <b class=\"bb_b\">Артем Сторчак</b>, <b class=\"bb_b\">Максим Дорошенко</b>, <b class=\"bb_b\">Богдан Чеботарьов</b>, <b class=\"bb_b\">Євген Богданович</b>та <b class=\"bb_b\">Віктор Бондаренко</b>. Усі виконавці навчаються у класі викладача <b class=\"bb_b\">Анатолія Коваленка</b>.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://vch-uman.in.ua/v-umani-vidbudetsja-koncert-gitarnoi/" target="_blank">За матеріалами "vch-uman.in.ua"</a></p></div>', 1, '2019-10-31 20:40:12.39517', '''00'':48 ''1'':30 ''15'':47 ''25'':21 ''align'':55,143 ''b'':17,20,64,67,90,93,96,99,102,105,108,111,114,117,121,124,133,136 ''bb'':15,19,53,59,66,82,92,98,104,110,116,123,135,141 ''center'':56 ''class'':14,18,52,58,65,81,91,97,103,109,115,122,134,140 ''facebook'':79 ''p'':13,16,51,54,57,60,80,83,139,142 ''right'':144 ''vch-uman.in.ua'':147 ''анатолі'':137 ''арт'':100 ''богда'':112 ''богданович'':119 ''бондаренк'':126 ''біліченк'':95 ''викладач'':132 ''виконавці'':128 ''виконанні'':36 ''виступатимут'':86 ''вхід'':49 ''відбудет'':3B,9C,31 ''віктор'':125 ''вільни'':50 ''гітарної'':5B,11C,33 ''демуцьк'':44,76 ''дитячої'':27 ''дорошенк'':107 ''жовтн'':22 ''залі'':25 ''йдет'':63 ''класі'':131 ''коваленк'':138 ''концерт'':4B,10C,32 ''концертні'':24 ''макс'':106 ''матеріал'':146 ''музик'':6B,12C,34 ''музичн'':40,72,88 ''музичної'':28 ''навчают'':129 ''обласн'':39,71 ''порфирі'':43,75 ''початок'':45 ''світлан'':94 ''соцмережі'':78 ''сторчак'':101 ''сторінці'':69 ''студент'':87 ''студентів'':37 ''сцені'':85 ''та'':120 ''уманськ'':38,70 ''уманської'':26 ''умані'':2B,8C ''усі'':127 ''училищ'':41,73,89 ''це'':62 ''чеботар'':113 ''школ'':29 ''євген'':118 ''імені'':42,74', '', 1, 1, 0, 0, 1, 'https://vch-uman.in.ua/v-umani-vidbudetsja-koncert-gitarnoi/
