INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (59, 'Золотоніські спортсмени привезли 10 медалей із обласної першості кіокушин карате [ФОТО]', '4a1559b88666225b73206f149c674170', 'Золотоніські спортсмени привезли 10 медалей із обласної першості кіокушин карате [ФОТО]', 'Шість перемог, два срібла і дві бронзи: таким результатом відзначились юні золотоніські спортсмени на 20-му чемпіонаті області з кіокушин карате. Першість, на якій хлопцям вдалося зібрати цілий букет нагород, відбулася минулого вік-енду у Черкасах. Серед когорти вихованців тренера Олексія Дробязка варто відзначити шістьох переможців чемпіонату. Серед них – дебютант подібних змагань Микита Ткаченко, котрий свій фінальний поєдинок завершив достроково – нокаутом. Його більш досвідчений колега Костянтин Озірський відзначився нокаутами у всіх своїх боях, Данило Волков достроково завершив лише фінал, проте під час турнірної боротьби проявив чимало духу, щоразу перемагаючи у сильніших суперників. Аналогічну силу волі проявив Віталій Сушко. Артур Папоян та Денис Старіков усі поєдинки закінчили достроково. Останній ще й продемонстрував найкращу техніку бою. Другі місця дісталися золотонісцям Георгію Дробязко та Богдану Свинчуку, бронза у Владислава Новика та Арсенія Макаренка, котрий свій перший бій виграв нокаутом, незважаючи на чималу різницю у вазі.', '<p class=\"bb_p\">Шість перемог, два срібла і дві бронзи: таким результатом відзначились юні золотоніські спортсмени на 20-му чемпіонаті області з кіокушин карате. Першість, на якій хлопцям вдалося зібрати цілий букет нагород, відбулася минулого вік-енду у Черкасах.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-40-18_547.png" alt="" title="" /></p>
<p class=\"bb_p\">Серед когорти вихованців тренера <b class=\"bb_b\">Олексія Дробязка</b> варто відзначити шістьох переможців чемпіонату. Серед них – дебютант подібних змагань <b class=\"bb_b\">Микита Ткаченко</b>, котрий свій фінальний поєдинок завершив достроково – нокаутом. Його більш досвідчений колега <b class=\"bb_b\">Костянтин Озірський</b> відзначився нокаутами у всіх своїх боях, <b class=\"bb_b\">Данило Волков</b> достроково завершив лише фінал, проте під час турнірної боротьби проявив чимало духу, щоразу перемагаючи у сильніших суперників. Аналогічну силу волі проявив <b class=\"bb_b\">Віталій Сушко</b>. <b class=\"bb_b\">Артур Папоян</b> та <b class=\"bb_b\">Денис Старіков</b> усі поєдинки закінчили достроково. Останній ще й продемонстрував найкращу техніку бою.</p>
<p class=\"bb_p\">Другі місця дісталися золотонісцям <b class=\"bb_b\">Георгію Дробязко</b> та <b class=\"bb_b\">Богдану Свинчуку</b>, бронза у <b class=\"bb_b\">Владислава Новика</b> та <b class=\"bb_b\">Арсенія Макаренка</b>, котрий свій перший бій виграв нокаутом, незважаючи на чималу різницю у вазі.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://zolotonosha.ck.ua/2019/10/30/zolotonis-ki-sportsmeny-pry-vezly-10-medalej-iz-oblasnoyi-pershosti-kiokushy-n-karate-foto/" target="_blank">За матеріалами "zolotonosha.ck.ua"</a></p></div>', 1, '2019-10-31 20:40:21.237112', '''10'':4B,15C ''20'':41 ''align'':68,232 ''b'':78,81,94,97,111,114,123,126,150,153,156,159,163,166,188,191,195,198,203,206,210,213 ''bb'':25,66,72,80,96,113,125,152,158,165,182,190,197,205,212,230 ''center'':69 ''class'':24,65,71,79,95,112,124,151,157,164,181,189,196,204,211,229 ''p'':23,26,64,67,70,73,180,183,228,231 ''right'':233 ''zolotonosha.ck.ua'':236 ''аналогічн'':146 ''арсені'':214 ''артур'':160 ''бо'':122,179 ''богдан'':199 ''боротьб'':137 ''бронз'':33,201 ''букет'':55 ''бій'':219 ''більш'':108 ''вазі'':227 ''варт'':84 ''вдал'':52 ''вигра'':220 ''вихованців'':76 ''владислав'':207 ''волк'':128 ''волі'':148 ''всіх'':120 ''відбул'':57 ''відзнач'':36 ''відзначив'':117 ''відзначит'':85 ''вік'':60 ''вік-енд'':59 ''віталі'':154 ''георгі'':192 ''дан'':127 ''дві'':32 ''дебютант'':91 ''денис'':167 ''досвідчен'':109 ''достроков'':105,129,172 ''дробязк'':83,193 ''другі'':184 ''дух'':140 ''дістал'':186 ''енд'':61 ''з'':45 ''заверш'':104,130 ''закінч'':171 ''змаган'':93 ''золотонісц'':187 ''золотоніські'':1B,12C,38 ''зібрат'':53 ''й'':175 ''йог'':107 ''карат'':10B,21C,47 ''когорт'':75 ''колег'':110 ''костянтин'':115 ''котр'':100,216 ''кіокушин'':9B,20C,46 ''лиш'':131 ''макаренк'':215 ''матеріал'':235 ''медал'':5B,16C ''микит'':98 ''минул'':58 ''му'':42 ''місця'':185 ''нагород'':56 ''найкращ'':177 ''незважаюч'':222 ''новик'':208 ''нокаут'':106,118,221 ''обласно