INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (63, 'Представники Канади та Великої Британії навчатимуть золотоніських школярів', 'e6f9b1733b96c2fe2bc5ebf3c14f826d', 'Представники Канади та Великої Британії навчатимуть золотоніських школярів', 'Золотоноша зміцнює міжнародні зв’язки. Минуло лише кілька днів, як місто відвідали меценати з Німеччини, а вже 29 жовтня до міської ради завітали представники Великої Британії та Канади. Про це інформує Золотоноша.City. На зустрічі з мером Золотоноші Віталієм Войцехівським гості обговорили перспективи розвитку міської освіти й накреслили спільний план дій щодо реалізації освітніх проектів. А гості з Великої Британії цей тиждень проведуть у таборі «MAXIMUM».  – Нові партнери табору – представники центру “CYE” з Великої Британії – приїхала на нашу осінню програму «Британські канікули», яка проходитиме з 28 жовтня по 1 листопада, – розповідає очільник табору «МAXIMUM» Віталій Собко. – Це – перша програма, яку ми проводимо в такому форматі. Діти практикуватимуться в англійській мові, братимуть участь у цікавих англомовних проектах та обговорюватимуть тему заїду – «Традиції в нашому житті». Взаємовигідна співпраця допоможе в розвитку навчальних проектів для дітей шкільного віку.', '<p class=\"bb_p\">Золотоноша зміцнює міжнародні зв’язки. Минуло лише кілька днів, як місто відвідали меценати з Німеччини, а вже 29 жовтня до міської ради завітали представники Великої Британії та Канади. Про це інформує Золотоноша.City.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-40-24_270.png" alt="" title="" /></p>
<p class=\"bb_p\">На зустрічі з мером Золотоноші <b class=\"bb_b\">Віталієм Войцехівським</b> гості обговорили перспективи розвитку міської освіти й накреслили спільний план дій щодо реалізації освітніх проектів. А гості з Великої Британії цей тиждень проведуть у таборі «MAXIMUM».</p>
<p class=\"bb_p\"> – Нові партнери табору – представники центру “CYE” з Великої Британії – приїхала на нашу осінню програму «Британські канікули», яка проходитиме з 28 жовтня по 1 листопада, – розповідає очільник табору «МAXIMUM» <b class=\"bb_b\">Віталій Собко</b>. – Це – перша програма, яку ми проводимо в такому форматі. Діти практикуватимуться в англійській мові, братимуть участь у цікавих англомовних проектах та обговорюватимуть тему заїду – «Традиції в нашому житті». Взаємовигідна співпраця допоможе в розвитку навчальних проектів для дітей шкільного віку.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://zolotonosha.ck.ua/2019/10/30/predstavny-ky-kanady-ta-vely-koyi-bry-taniyi-navchaty-mut-zolotonis-ky-h-shkolyariv/" target="_blank">За матеріалами "zolotonosha.ck.ua"</a></p></div>', 1, '2019-10-31 20:40:27.633099', '''1'':127 ''28'':124 ''29'':38 ''align'':58,182 ''b'':69,72,133,136 ''bb'':19,56,62,71,103,135,180 ''center'':59 ''citi'':53 ''class'':18,55,61,70,102,134,179 ''cye'':110 ''maximum'':100 ''p'':17,20,54,57,60,63,101,104,178,181 ''right'':183 ''zolotonosha.ck.ua'':186 ''англомовн'':157 ''англійські'':151 ''братимут'':153 ''британські'':119 ''британії'':5B,13C,46,94,113 ''великої'':4B,12C,45,93,112 ''вже'':37 ''взаємовигідн'':167 ''войцехівськ'':74 ''відвідал'':32 ''віку'':177 ''віталі'':137 ''віталієм'':73 ''гості'':75,91 ''днів'':29 ''допомож'':169 ''дій'':85 ''діте'':175 ''діти'':148 ''житті'':166 ''жовтн'':39,125 ''з'':34,66,92,111,123 ''завіта'':43 ''заїд'':162 ''зв'':24 ''зміцнює'':22 ''золотонош'':21,52 ''золотоноші'':68 ''золотоніськ'':7B,15C ''зустрічі'':65 ''й'':81 ''канад'':2B,10C,48 ''канікул'':120 ''кілька'':28 ''листопад'':128 ''лиш'':27 ''мaximum'':132 ''матеріал'':185 ''мер'':67 ''меценат'':33 ''ми'':143 ''минул'':26 ''мові'':152 ''міжнародні'':23 ''місто'':31 ''міської'':41,79 ''навчальн'':172 ''навчатимут'':6B,14C ''накресл'':82 ''наш'':116,165 ''нові'':105 ''німеччин'':35 ''обговор'':76 ''