INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (84, 'Іще один шкільний автобус отримали школярі Тальнівщини', '8c3afe03f2eac0e8173b2aee67e24fa5', 'Іще один шкільний автобус отримали школярі Тальнівщини', 'На Тальнівщині отримали ще один шкільний автобус, який використовуватимуть для підвезення дітей з села Раманівка до  Мошурівської ЗОШ І-ІІІ ст. Вартість транспорту – 1 млн 855 тис. грн. Автобус придбали на умовах співфінансування (50х50) з державного та районного бюджетів за сприяння депутатів Черкаської обласної ради.  Депутати районної ради також підтримали ініціативу щодо виділення 925 тис грн із районного бюджету. Новий шкільний автобус &nbsp;«Еталон» розрахований на 30 місць, має додаткові рівні безпеки, зокрема обмеження у швидкості руху, звукову та світлову сигналізацію, аварійні кнопки, спеціальні сидіння та зручні сходинки.&nbsp;Тож школярі матимуть можливість&nbsp;зручно і комфортно&nbsp;добиратися&nbsp;до навчального закладу. За інформацією Тальнівської районної адміністрації', '<p class=\"bb_p\"><b class=\"bb_b\">На Тальнівщині отримали ще один шкільний автобус, який використовуватимуть для підвезення дітей з села Раманівка до  Мошурівської ЗОШ І-ІІІ ст.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-41-42_646.png" alt="" title="" /></p>
<p class=\"bb_p\">Вартість транспорту – 1 млн 855 тис. грн. Автобус придбали на умовах співфінансування (50х50) з державного та районного бюджетів за сприяння депутатів Черкаської обласної ради.  Депутати районної ради також підтримали ініціативу щодо виділення 925 тис грн із районного бюджету.</p>
<p class=\"bb_p\">Новий шкільний автобус &nbsp;«Еталон» розрахований на 30 місць, має додаткові рівні безпеки, зокрема обмеження у швидкості руху, звукову та світлову сигналізацію, аварійні кнопки, спеціальні сидіння та зручні сходинки.&nbsp;Тож школярі матимуть можливість&nbsp;зручно і комфортно&nbsp;добиратися&nbsp;до навчального закладу.</p>
<p class=\"bb_p\">За інформацією Тальнівської районної адміністрації</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://provce.ck.ua/ishche-odyn-shkil-nyy-avtobus-otrymaly-shkoliari-tal-nivshchyny/" target="_blank">За матеріалами "provce.ck.ua"</a></p></div>', 1, '2019-10-31 20:41:45.754957', '''1'':57 ''30'':103 ''50х50'':67 ''855'':59 ''925'':87 ''align'':49,149 ''b'':19,22 ''bb'':17,21,47,53,95,138,147 ''center'':50 ''class'':16,20,46,52,94,137,146 ''p'':15,18,45,48,51,54,93,96,136,139,145,148 ''provce.ck.ua'':153 ''right'':150 ''аварійні'':118 ''автобус'':4B,11C,29,62,99 ''адміністрації'':144 ''безпек'':108 ''бюджет'':92 ''бюджетів'':72 ''вартіст'':55 ''виділен'':86 ''використовуватимут'':31 ''грн'':61,89 ''депутат'':79 ''депутатів'':75 ''державн'':69 ''добират'':132 ''додаткові'':106 ''діте'':34 ''еталон'':100 ''з'':35,68 ''заклад'':135 ''звуков'':114 ''зокрем'':109 ''зош'':40 ''зручн'':129 ''зручні'':123 ''кнопк'':119 ''комфортн'':131 ''матеріал'':152 ''матимут'':127 ''має'':105 ''млн'':58 ''можливіст'':128 ''мошурівської'':39 ''місць'':104 ''навчальн'':134 ''нов'':97 ''обласної'':77 ''обмежен'':110 ''отрима'':5B,12C,25 ''придба'':63 ''підвезен'':33 ''підтрима'':83 ''рад'':78,81 ''район'':71,91 ''районної'':80,143 ''раманівк'':37 ''розрахован'':101 ''рух'':113 ''рівні'':107 ''світлов'':116 ''сел'':36 ''сигналізаці'':117 ''сидін'':121 ''спеціальні'':120 ''сприян'':74 ''співфінансуван'':66 ''ст'':44 ''сходинк'':124 ''та'':70,115,122 ''також'':82 ''тальнівської'':142 ''тальнівщин'':7B,14C ''тальнівщині'':24 ''тис'':60,88 ''тож'':125 ''транспорт'':56 ''умов'':65 ''черкаської'':76 ''швидкості'':112 ''школярі'':6B,13C,126 ''шкільни'':3B,10C,28,98 ''ще'':26 ''щод'':85 ''як'':30 ''і'':42,130 ''і-ііі'':41 ''із'':90 ''інформаціє'':141 ''ініціатив'':84 ''іще'':1B,8C ''ііі'':43', '', 1, 1, 0, 0, 1, 'https://provce.ck.ua/ishche-odyn-shkil-nyy-avtobus-otrymaly-shkoliari-tal-nivshchyny/
