INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (114, 'Тальнівський міський голова склав повноваження', '11c6355fe86ec6ed47d505e471ec5be0', 'Тальнівський міський голова склав повноваження', '30 жовтня 2019 року на сесії Тальнівської міської ради, о 10 годині ранку, Олексію Юрченко склав повноваження міського голови. У свіїй заяві екс-голова зіслався на стан здоров’я. Що далі чекає Тальнівську громаду – розповімо згодом.Про це пише TalneOTG.com', '<p class=\"bb_p\">30 жовтня 2019 року на сесії Тальнівської міської ради, о 10 годині ранку, Олексію Юрченко склав повноваження міського голови. У свіїй заяві екс-голова зіслався на стан здоров’я. Що далі чекає Тальнівську громаду – розповімо згодом.</p><p class=\"bb_p\">Про це пише TalneOTG.com</p>
