INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (17, 'Огляд Meizu M10 – Чистий Android чи Flyme OS?', '1aea02f3535ef295ef62a308b057e64a', 'Огляд Meizu M10 – Чистий Android чи Flyme OS?', 'Компанія Meizu. Думаю багато хто з вас вже почав забувати про таку компанія, яка випускає смартфони, а ще рік тому вона твердо конкурувала з Xiaomi. Але пройшов деякий час, компанія все переосмислила та вирішали зайти на український ринок та знову ж таки почати боротьбу за покупців, чи вийде в компанії дізнаємось згодом, а сьогодні в моїх руках Meizu M10, смартфон який працює під управлінням чистого андроїд, має приємний стильний дизайн , ну що ж давайте подивимось чи вийшло в компанії Meizu здивувати своїх фанів. Дисплей: 6,5 “, TFT, 1600 ? 720 пікселів, співвідношення сторін 19,5: 9, 270 ppi Чіпсет: MediaTek MT6757CD Helio P25, 8-ядерний, 4 ядра Cortex-A53 з тактовою частотою 2,4 ГГц і 4 ядра Cortex-A53 з тактовою частотою 1,7 ГГц Бездротові мережі: Wi-Fi 802.11 a / b / g / n / ac, Bluetooth 4.2, GPS (A-GPS, GLONASS) Основна камера: потрійна, головний модуль 13 Мп, f / 2.2, PDAF; 2 Мп, f / 2.2; 2 Мп, f / 2.2', '<p class=\"bb_p\">Компанія Meizu. Думаю багато хто з вас вже почав забувати про таку компанія, яка випускає смартфони, а ще рік тому вона твердо конкурувала з Xiaomi. Але пройшов деякий час, компанія все переосмислила та вирішали зайти на український ринок та знову ж таки почати боротьбу за покупців, чи вийде в компанії дізнаємось згодом, а сьогодні в моїх руках <b class=\"bb_b\">Meizu M10</b>, смартфон який працює під управлінням чистого андроїд, має приємний стильний дизайн , ну що ж давайте подивимось чи вийшло в компанії <b class=\"bb_b\">Meizu</b> здивувати своїх фанів.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-28-54_336.png" alt="" title="" /></p>
<p class=\"bb_p\">Дисплей: 6,5 “, TFT, 1600 ? 720 пікселів, співвідношення сторін 19,5: 9, 270 ppi</p>
<div class="bb_youtube"><iframe width="640" src="https://www.youtube.com/embed/bxh9-xDv8Tg" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe></div>
<p class=\"bb_p\">Чіпсет: MediaTek MT6757CD Helio P25, 8-ядерний, 4 ядра Cortex-A53 з тактовою частотою 2,4 ГГц і 4 ядра Cortex-A53 з тактовою частотою 1,7 ГГц</p>
<p class=\"bb_p\">Бездротові мережі: Wi-Fi 802.11 a / b / g / n / ac, Bluetooth 4.2, GPS (A-GPS, GLONASS)</p>
<p class=\"bb_p\">Основна камера: потрійна, головний модуль 13 Мп, f / 2.2, PDAF; 2 Мп, f / 2.2; 2 Мп, f / 2.2</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://playua.net/oglyad-meizu-m10-chystyj-android-chy-flyme-os/" target="_blank">За матеріалами "playua.net"</a></p></div>', 1, '2019-10-31 20:28:57.79587', '''1'':167 ''13'':201 ''1600'':126 ''19'':131 ''2'':155,206,210 ''2.2'':204,209,213 ''270'':134 ''4'':147,156,159 ''4.2'':186 ''5'':124,132 ''6'':123 ''7'':168 ''720'':127 ''8'':145 ''802.11'':179 ''9'':133 ''a-gp'':188 ''a53'':151,163 ''ac'':184 ''align'':116,218 ''android'':5B,13C ''b'':78,81,104,107,181 ''bb'':19,80,106,114,120,138,172,194,216 ''bluetooth'':185 ''center'':117 ''class'':18,79,105,113,119,137,171,193,215 ''cortex'':150,162 ''cortex-a53'':149,161 ''f'':203,208,212 ''fi'':178 ''flyme'':7B,15C ''g'':182 ''glonass'':191 ''gps'':187,190 ''helio'':143 ''m10'':3B,11C,83 ''mediatek'':141 ''meizu'':2B,10C,22,82,108 ''mt6757cd'':142 ''n'':183 ''os'':8B,16C ''p'':17,20,112,115,118,121,136,139,170,173,192,195,214,217 ''p25'':144 ''pdaf'':205 ''playua.net'':222 ''ppi'':135 ''right'':219 ''tft'':125 ''wi'':177 ''wi-fi'':176 ''xiaomi'':45 ''ал'':46 ''андроїд'':90 ''багат'':24 ''бездротові'':174 ''боротьб'':64 ''вже'':28 ''вийд'':68 ''вийшл'':101 ''випускає'':35 ''виріша'':54 ''вон'':41 ''ггц'':157,169 ''головн'':199 ''дава'':98 ''деяк'':48 ''дизайн'':94 ''диспл'':122 ''дума'':23 ''дізнаєм'':71 ''з'':26,44,152,164 ''забуват'':30 ''зайт'':55 ''згод'':72 ''здивуват'':109 ''знов'':60 ''камер'':197 ''компані'':21,33,50 ''компанії'':70,103 ''конкурува'':43 ''матеріал'':221 ''має'':91 ''мережі'':175 ''модул'':200 ''моїх'':76 ''мп'':202,207,211 ''огляд'':1B,9C ''основн'':196 ''переосмисл'':52 ''подивим'':99 ''покупців'':66 ''потрійн'':198 ''поча'':29 ''почат'':63 ''працює'':86 ''приємн'':92 ''пройш'':47 ''під'':87 ''пікселів'':128 ''ринок'':58 ''рук'':77 ''рік'':39 ''своїх'':110 ''смартфон'':36,84 ''співвідношен'':129 '