INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (20, 'Не варто чекати на новий Battlefield наступного року', '36e97bc2688d72d4f701e7bf85007078', 'Не варто чекати на новий Battlefield наступного року', 'EA у своєму фінансовому звіті оголосила, що у 2020-ому році не буде нової Battlefield. Повернення відомої франшизи відбудеться лише у 2022 фінансовому році. 2022 фінансовий рік розпочнеться у квітні 2021-го року, а закінчиться у березні 2022-го. Саме в цей період і варто чекати на повернення франшизи. Нагадаємо, що остання гра серії, Battlefield V, вийшла у листопаді минулого року.  Головним сервіс-шутером EA на наступний рік стане Apex  Legends, який недавно досяг 70 мільйонів гравців по всьому світу. По грі від Respawn вже планується вихід на Китайський ринок, розвиток Apex Legends, як кіберспортивної дисципліни, а також вихід на нових платформах, у тому числі на мобільних пристроях.', '<p class=\"bb_p\">EA у своєму фінансовому звіті оголосила, що у 2020-ому році не буде нової Battlefield. Повернення відомої франшизи відбудеться лише у 2022 фінансовому році.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-29-07_443.png" alt="" title="" /></p>
<p class=\"bb_p\">2022 фінансовий рік розпочнеться у квітні 2021-го року, а закінчиться у березні 2022-го. Саме в цей період і варто чекати на повернення франшизи. Нагадаємо, що остання гра серії, Battlefield V, вийшла у листопаді минулого року. </p>
<p class=\"bb_p\">Головним сервіс-шутером EA на наступний рік стане Apex  Legends, який недавно досяг 70 мільйонів гравців по всьому світу. По грі від Respawn вже планується вихід на Китайський ринок, розвиток Apex Legends, як кіберспортивної дисципліни, а також вихід на нових платформах, у тому числі на мобільних пристроях.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://playua.net/ne-varto-chekaty-na-novyj-battlefield-nastupnogo-roku/" target="_blank">За матеріалами "playua.net"</a></p></div>', 1, '2019-10-31 20:29:08.073', '''2020'':29 ''2021'':61 ''2022'':42,55,68 ''70'':110 ''align'':49,148 ''apex'':105,127 ''battlefield'':6B,14C,35,85 ''bb'':19,47,53,94,146 ''center'':50 ''class'':18,46,52,93,145 ''ea'':21,100 ''legend'':106,128 ''p'':17,20,45,48,51,54,92,95,144,147 ''playua.net'':152 ''respawn'':119 ''right'':149 ''v'':86 ''березні'':67 ''буд'':33 ''варт'':2B,10C,75 ''вже'':120 ''вийшл'':87 ''вихід'':122,134 ''всьом'':114 ''від'':118 ''відбудет'':39 ''відомої'':37 ''го'':62,69 ''головн'':96 ''гра'':83 ''гравців'':112 ''грі'':117 ''дисциплін'':131 ''досяг'':109 ''закінч'':65 ''звіті'':25 ''квітні'':60 ''китайськ'':124 ''кіберспортивної'':130 ''листопаді'':89 ''лиш'':40 ''матеріал'':151 ''минул'':90 ''мобільн'':142 ''мільйонів'':111 ''нагадаєм'':80 ''наступн'':7B,15C,102 ''недавн'':108 ''нов'':5B,13C,136 ''нової'':34 ''оголос'':26 ''ом'':30 ''остан'':82 ''період'':73 ''плануєт'':121 ''платформ'':137 ''повернен'':36,78 ''пристро'':143 ''ринок'':125 ''розвиток'':126 ''розпочнет'':58 ''рок'':8B,16C,63,91 ''році'':31,44 ''рік'':57,103 ''сам'':70 ''своєм'':23 ''світу'':115 ''сервіс'':98 ''сервіс-шутер'':97 ''серії'':84 ''стан'':104 ''також'':133 ''том'':139 ''франшиз'':38,79 ''фінансов'':24,43,56 ''це'':72 ''чекат'':3B,11C,76 ''числі'':140 ''шутер'':99 ''що'':27,81 ''як'':107,129 ''і'':74', '', 1, 1, 0, 0, 1, 'https://playua.net/ne-varto-chekaty-na-novyj-battlefield-nastupnogo-roku/
