INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (23, 'Фільм «Відьмак: Полювання» тепло зустріли у Львові', 'e8108db8265f01158abd05fa3a5cce69', 'Фільм «Відьмак: Полювання» тепло зустріли у Львові', 'Минулої неділі у книгарні «Книжковий лев» у Львові відбувся показ фанатської стрічки «Відьмак: Полювання». Зібралася повна зала, тож було тепло, затишно і гамірно під час обговорення процесу створення фільму. Серед присутніх були і актори, що фігурують у короткому метрі, і доброчинці проекту, і просто зацікавлені. Показ самого фільму супроводжувався радісними вигуками, а фінальні титри було зустріто гучними оплесками. На цьому вечір не зупинився і глядачі розпитували учасників проекту „як це було“. Автори стрічки наголошували, що пишаються проробленою роботою і сподіваються, що «Відьмак: Полювання» надихатиме інших українських фанатів створювати своє коротке і довге кіно. Найближчим часом очікується заключний закритий показ стрічки у Запоріжжі. Очікуйте на анонс. А після запорізької прем’єри «Відьмак: Полювання» можна буде побачити на YouTube.', '<p class=\"bb_p\">Минулої неділі у книгарні «Книжковий лев» у Львові відбувся показ фанатської стрічки «Відьмак: Полювання». Зібралася повна зала, тож було тепло, затишно і гамірно під час обговорення процесу створення фільму.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-29-19_2.png" alt="" title="" /></p>
<p class=\"bb_p\">Серед присутніх були і актори, що фігурують у короткому метрі, і доброчинці проекту, і просто зацікавлені.</p>
<p class=\"bb_p\">Показ самого фільму супроводжувався радісними вигуками, а фінальні титри було зустріто гучними оплесками.</p>
<p class=\"bb_p\">На цьому вечір не зупинився і глядачі розпитували учасників проекту „як це було“. Автори стрічки наголошували, що пишаються проробленою роботою і сподіваються, що «Відьмак: Полювання» надихатиме інших українських фанатів створювати своє коротке і довге кіно.</p>
<p class=\"bb_p\">Найближчим часом очікується заключний закритий показ стрічки у Запоріжжі. Очікуйте на анонс. А після запорізької прем’єри «Відьмак: Полювання» можна буде побачити на YouTube.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://playua.net/film-vidmak-polyuvannya-teplo-zustrily-u-lvovi/" target="_blank">За матеріалами "playua.net"</a></p></div>', 1, '2019-10-31 20:29:23.327922', '''align'':52,162 ''bb'':17,50,56,76,93,132,160 ''center'':53 ''class'':16,49,55,75,92,131,159 ''p'':15,18,48,51,54,57,74,77,91,94,130,133,158,161 ''playua.net'':166 ''right'':163 ''youtub'':157 ''автор'':108 ''актор'':62 ''анонс'':145 ''буд'':154 ''бул'':37,60,87,107 ''вечір'':97 ''вигук'':83 ''відбув'':27 ''відьмак'':2B,9C,31,118,151 ''гамірн'':41 ''глядачі'':101 ''гучн'':89 ''доброчинці'':69 ''довг'':128 ''заключн'':137 ''закрит'':138 ''зал'':35 ''запоріжжі'':142 ''запорізької'':148 ''затишн'':39 ''зацікавлені'':73 ''зупинив'':99 ''зустріл'':5B,12C ''зустріт'':88 ''зібрал'':33 ''книгарні'':22 ''книжков'':23 ''коротк'':66,126 ''кіно'':129 ''лев'':24 ''львові'':7B,14C,26 ''матеріал'':165 ''метрі'':67 ''минулої'':19 ''можн'':153 ''наголошува'':110 ''надихатим'':120 ''найближч'':134 ''неділі'':20 ''обговорен'':44 ''оплеск'':90 ''очік'':143 ''очікуєт'':136 ''пишают'':112 ''побачит'':155 ''повн'':34 ''показ'':28,78,139 ''полюван'':3B,10C,32,119,152 ''прем'':149 ''присутніх'':59 ''проект'':70,104 ''пророблен'':113 ''прост'':72 ''процес'':45 ''під'':42 ''після'':147 ''радісн'':82 ''робот'':114 ''розпитува'':102 ''сам'':79 ''своє'':125 ''серед'':58 ''сподівают'':116 ''створен'':46 ''створюват'':124 ''стрічки'':30,109,140 ''супроводжував'':81 ''тепл'':4B,11C,38 ''титр'':86 ''тож'':36 ''українськ'':122 ''учасників'':103 ''фанатської'':29 ''фанатів'':123 ''фігуруют'':64 ''фільм'':1B,8C ''фільму'':47,80 ''фінальні'':85 ''це'':106 ''цьом'':96 ''час'':43,135 ''що'':63,111,117 ''як'':105 ''єри'':150 ''і'':40,61,68,71,100,115,127 ''інших'':121', 