INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (24, 'EA повертається до Steam –  першою доступною грою стала Star Wars Jedi: Fallen Order', 'eef0ff6222ddee4ad0d3740b98445bd0', 'EA повертається до Steam –  першою доступною грою стала Star Wars Jedi: Fallen Order', 'Про партнерство Valve і Electronic Arts оголосили в офіційному прес–релізі. Всі ігри компанії в майбутньому будуть з’являтися в магазині Steam, а першою стала Jedi: Fallen Order – на неї вже можна оформити попереднє замовлення. Ігри додаватимуть в сервіс Valve поступово. Крім того, навесні 2020 року EA запустить в Steam і підписку Access, яка дає доступ до бібліотеки ігор, а також дозволяє почати грати в окремі новинки до повноцінного релізу. Це буде перший передплатний сервіс, доступний в крамниці.', '<p class=\"bb_p\">Про партнерство Valve і Electronic Arts оголосили в офіційному прес–релізі. Всі ігри компанії в майбутньому будуть з’являтися в магазині Steam, а першою стала Jedi: Fallen Order – на неї вже можна <b class=\"bb_b\">оформити</b> попереднє замовлення. Ігри додаватимуть в сервіс Valve поступово.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-29-23_446.png" alt="" title="" /></p>
<p class=\"bb_p\">Крім того, навесні 2020 року EA запустить в Steam і підписку Access, яка дає доступ до бібліотеки ігор, а також дозволяє почати грати в окремі новинки до повноцінного релізу. Це буде перший передплатний сервіс, доступний в крамниці.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://playua.net/ea-povertayetsya-do-steam-pershoyu-dostupnoyu-groyu-stala-star-wars-jedi-fallen-order/" target="_blank">За матеріалами "playua.net"</a></p></div>', 1, '2019-10-31 20:29:24.727808', '''2020'':89 ''access'':97 ''align'':80,127 ''art'':36 ''b'':63,66 ''bb'':29,65,78,84,125 ''center'':81 ''class'':28,64,77,83,124 ''ea'':1B,14C,91 ''electron'':35 ''fallen'':12B,25C,57 ''jedi'':11B,24C,56 ''order'':13B,26C,58 ''p'':27,30,76,79,82,85,123,126 ''playua.net'':131 ''right'':128 ''star'':9B,22C ''steam'':4B,17C,52,94 ''valv'':33,74 ''war'':10B,23C ''буд'':116 ''будут'':47 ''бібліотек'':102 ''вже'':61 ''всі'':42 ''грат'':108 ''гро'':7B,20C ''дає'':99 ''додаватимут'':71 ''дозволяє'':106 ''доступ'':100 ''доступн'':6B,19C,120 ''з'':48 ''замовлен'':69 ''запуст'':92 ''компанії'':44 ''крамниці'':122 ''крім'':86 ''магазині'':51 ''майбутн'':46 ''матеріал'':130 ''можн'':62 ''навесні'':88 ''неї'':60 ''новинк'':111 ''оголос'':37 ''окремі'':110 ''оформит'':67 ''офіційн'':39 ''партнерств'':32 ''передплатн'':118 ''перш'':5B,18C,54,117 ''повертаєт'':2B,15C ''повноцін'':113 ''попереднє'':68 ''поступов'':75 ''почат'':107 ''прес'':40 ''підписк'':96 ''реліз'':114 ''релізі'':41 ''рок'':90 ''сервіс'':73,119 ''стал'':8B,21C,55 ''також'':105 ''це'':115 ''являт'':49 ''як'':98 ''і'':34,95 ''ігор'':103 ''ігри'':43,70', '', 1, 1, 0, 0, 1, 'https://playua.net/ea-povertayetsya-do-steam-pershoyu-dostupnoyu-groyu-stala-star-wars-jedi-fallen-order/
