INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (28, 'EMUI 10 стає доступною ще для 8 смартфонів Huawei', '382a5096771a4d68f45a53ef4322c5b4', 'EMUI 10 стає доступною ще для 8 смартфонів Huawei', 'Ще тиждень тому в Китаї стартувало відкрите бета-тестування прошивок EMUI 10 та Magic UI 3.0. Вони створені на базі операційної системи Android 10 і стали доступними для восьми моделей смартфонів Huawei та Honor. А сьогодні компанія Huawei офіційно повідомила про те, що випробувати бета-версії прошивки на базі Android 10 нарешті отримали можливість користувачі інших смартфонів компанії по всьому світі. Список смартфонів, які отримають EMUI 10 (бета): 10-а версія прошивки приносить оновлений призначений для користувача інтерфейс, повноекранну систему керування жестами, покращений нічний режим, який став більш комфортним для сприйняття тощо. Якщо ваш пристрій є в списку і ви хочете протестувати останню версію EMUI, вам спочатку потрібно завантажити додаток Huawei для участі в бета-тестуванні.', '<p class=\"bb_p\">Ще тиждень тому в Китаї стартувало відкрите бета-тестування прошивок <b class=\"bb_b\">EMUI 10</b> та <b class=\"bb_b\">Magic UI 3.0</b>. Вони створені на базі операційної системи Android 10 і стали доступними для восьми моделей смартфонів Huawei та Honor. А сьогодні компанія Huawei офіційно повідомила про те, що випробувати бета-версії прошивки на базі Android 10 нарешті отримали можливість користувачі інших смартфонів компанії по всьому світі.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-29-37_263.png" alt="" title="" /></p>
<p class=\"bb_p\">Список смартфонів, які отримають EMUI 10 (бета):</p>
