INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (34, 'У США хакерка видала себе, розповівши на інтернет-форумі про крадіжку даних з банку (відео)', '50577dcc9bc9ac7612ca0ed4760e2806', 'У США хакерка видала себе, розповівши на інтернет-форумі про крадіжку даних з банку (відео)', 'У банку наполохані випадком і вже обіцяють якнайшвидше покращити систему охорони клієнтської бази. З мережі американського банку поцупили дані понад 106 мільйонів клієнтів. Як йдеться в сюжеті ТСН, до рук зловмисника потрапили імена, особисті телефони та адреси, а також історії витрат. Шахрая піймали випадково – хвалько розбазікав про скоєне на форумі в Інтернеті. Як виявилось, це жінка. 33-річна Пейдж Томпсон раніше працювала інженером програмного забезпечення. Тепер за крадіжку їй світить до 5 років за ґратами, а ще – штраф у понад 250 тисяч доларів. У банку наполохані випадком і вже обіцяють якнайшвидше покращити систему охорони клієнтської бази.', '<p class=\"bb_p\">У банку наполохані випадком і вже обіцяють якнайшвидше покращити систему охорони клієнтської бази.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-29-59_707.png" alt="" title="" /></p>
<p class=\"bb_p\">З мережі американського банку поцупили дані понад 106 мільйонів клієнтів.</p>
<p class=\"bb_p\">Як йдеться в сюжеті ТСН, до рук зловмисника потрапили імена, особисті телефони та адреси, а також історії витрат. Шахрая піймали випадково – хвалько розбазікав про скоєне на форумі в Інтернеті.</p>
<p class=\"bb_p\">Як виявилось, це жінка. 33-річна Пейдж Томпсон раніше працювала інженером програмного забезпечення. Тепер за крадіжку їй світить до 5 років за ґратами, а ще – штраф у понад 250 тисяч доларів.</p>
<p class=\"bb_p\">У банку наполохані випадком і вже обіцяють якнайшвидше покращити систему охорони клієнтської бази.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://www.unian.ua/m/world/10635876-u-ssha-hakerka-vidala-sebe-rozpovivshi-na-internet-forumi-pro-kradizhku-danih-z-banku-video.html" target="_blank">За матеріалами "unian"</a></p></div>', 1, '2019-10-31 20:30:02.801865', '''106'':79 ''250'':147 ''33'':123 ''5'':138 ''align'':66,171 ''bb'':47,64,70,84,117,152,169 ''center'':67 ''class'':46,63,69,83,116,151,168 ''p'':45,48,62,65,68,71,82,85,115,118,150,153,167,170 ''right'':172 ''unian'':175 ''адрес'':99 ''американськ'':74 ''баз'':61,166 ''банк'':27B,43C,50,75,155 ''вже'':54,159 ''вида'':16B,32C ''випадк'':52,157 ''випадков'':106 ''витрат'':103 ''вияв'':120 ''віде'':28B,44C ''головні'':7A ''дан'':25B,41C ''дані'':77 ''дня'':6A ''доларів'':149 ''жінка'':122 ''з'':26B,42C,72 ''забезпечен'':131 ''зловмисник'':93 ''йдет'':87 ''київ'':12A ''клієнтської'':60,165 ''клієнтів'':81 ''крадіжк'':24B,40C,134 ''матеріал'':174 ''мережі'':73 ''мільйонів'':80 ''наполохані'':51,156 ''новин'':2A,3A,5A,8A,10A,11A ''обіцяют'':55,160 ''особисті'':96 ''охорон'':59,164 ''пейдж'':125 ''покращит'':57,162 ''понад'':78,146 ''потрап'':94 ''поцуп'':76 ''працюва'':128 ''програмн'':130 ''піймал'':105 ''раніш'':127 ''розбазіка'':108 ''розповівш'':18B,34C ''років'':139 ''рук'':92 ''річна'':124 ''свіжі'':9A ''світит'':136 ''сист'':58,163 ''скоєн'':110 ''сша'':14B,30C ''сюжеті'':89 ''та'':98 ''також'':101 ''телефон'':97 ''тепер'':132 ''тисяч'':148 ''томпсон'':126 ''тсн'':90 ''україн'':4A ''форумі'':22B,38C,112 ''хакер'':1A ''хакерк'':15B,31C ''хвальк'':107 ''це'':121 ''шахр'':104 ''штраф'':144 ''ще'':143 ''як'':86,119 ''якнайшвидш'':56,161 ''і'':53,158 ''імен'':95 ''інженер'':129 ''інтернет'':21B,37C ''інтернет-форумі'':20B,36C ''інтернеті'':114 ''історії'':102 ''їй'':135 ''ґрат'':141', 'хакер, новини, новини України, новини дня, головні новини, сві