INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (52, 'Xiaomi представила технологію рекордно швидкої зарядки смартфонів (відео)', '60a57eb1c69d98d41f939b4857cf9904', 'Xiaomi представила технологію рекордно швидкої зарядки смартфонів (відео)', 'Відео з презентацією нової технології опублікував генеральний директор Xiaomi Лін Бін. Китайська компанія Xiaomi представила технологію 100-ватної зарядки гаджетів з рекордною швидкістю поповнення ємності акумулятора. Як повідомляє 4PDA, відео з презентацією нової технології опублікував на своїй сторінці в Weibo генеральний директор Xiaomi Лін Бін. Ролик демонструє наочне порівняння швидкості зарядки неназваного смартфона компанії з батареєю на 4000 мАп і апарату OPPO (імовірно, R17 Pro). Останній оснащений двома акумуляторами загальною ємністю 3700 мАч і зарядним пристроєм потужністю 50 Вт. В ході тестування розряджений гаджет Xiaomi повністю відновив заряд акумулятора всього за 17 хвилин. Його суперник за той же час зміг зарядитися лише на 65%. Глава компанії натякнув, що познайомитися з технологією ближче користувачі зможуть уже незабаром в лінійці смартфонів Redmi.', '<p class=\"bb_p\">Відео з презентацією нової технології опублікував генеральний директор Xiaomi Лін Бін.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-31-30_693.png" alt="" title="" /></p>
<p class=\"bb_p\">Китайська компанія Xiaomi представила технологію 100-ватної зарядки гаджетів з рекордною швидкістю поповнення ємності акумулятора.</p>
<p class=\"bb_p\">Як повідомляє 4PDA, відео з презентацією нової технології опублікував на своїй сторінці в Weibo генеральний директор Xiaomi Лін Бін.</p>
<p class=\"bb_p\">Ролик демонструє наочне порівняння швидкості зарядки неназваного смартфона компанії з батареєю на 4000 мАп і апарату OPPO (імовірно, R17 Pro). Останній оснащений двома акумуляторами загальною ємністю 3700 мАч і зарядним пристроєм потужністю 50 Вт.</p>
<p class=\"bb_p\">В ході тестування розряджений гаджет Xiaomi повністю відновив заряд акумулятора всього за 17 хвилин. Його суперник за той же час зміг зарядитися лише на 65%.</p>
<p class=\"bb_p\">Глава компанії натякнув, що познайомитися з технологією ближче користувачі зможуть уже незабаром в лінійці смартфонів Redmi.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="https://www.unian.ua/m/economics/telecom/10493268-xiaomi-predstavila-tehnologiyu-rekordno-shvidkoji-zaryadki-smartfoniv-video.html" target="_blank">За матеріалами "unian"</a></p></div>', 1, '2019-10-31 20:31:31.858285', '''100'':59 ''17'':146 ''3700'':122 ''4000'':108 ''4pda'':75 ''50'':128 ''65'':158 ''align'':48,183 ''bb'':31,46,52,71,94,132,161,181 ''center'':49 ''class'':30,45,51,70,93,131,160,180 ''oppo'':112 ''p'':29,32,44,47,50,53,69,72,92,95,130,133,159,162,179,182 ''pro'':115 ''r17'':114 ''redmi'':178 ''right'':184 ''unian'':187 ''weibo'':86 ''xiaomi'':13B,21C,41,56,89,139 ''акумулятор'':68,119,143 ''апарат'':111 ''батареє'':106 ''ближч'':170 ''бін'':43,91 ''ватної'':60 ''всьог'':144 ''вт'':129 ''віде'':20B,28C,33,76 ''віднов'':141 ''гаджет'':138 ''гаджетів'':62 ''генеральн'':39,87 ''глав'':163 ''головні'':7A ''двом'':118 ''девайс'':1A ''демонструє'':97 ''директор'':40,88 ''дня'':6A ''з'':34,63,77,105,168 ''загальн'':120 ''заряд'':142 ''зарядит'':155 ''зарядк'':18B,26C,61,101 ''зарядн'':125 ''зможут'':172 ''зміг'':154 ''йог'':148 ''китайськ'':54 ''київ'':12A ''компані'':55 ''компанії'':104,164 ''користувачі'':171 ''лиш'':156 ''лін'':42,90 ''лінійці'':176 ''мап'':109 ''матеріал'':186 ''мач'':123 ''наочн'':98 ''натякнув'':165 ''незабар'':174 ''неназван'':102 ''новин'':2A,3A,5A,8A,10A,11A ''нової'':36,79 ''опублікува'':38,81 ''оснащен'':117 ''останні'':116 ''повніст'':140 ''повідомляє'':74 ''познайомит'':167 ''поповнен'':66 ''порівнян'':99 ''потужніст'':127 ''представ'':14B,22C,57 ''презентаціє'':35,78 ''пристроєм'':126 ''рекордн'':16B,24C,64 ''розряджен'':137 ''ролик'':96 ''свої'':83 ''свіжі'':9A ''смартфон'':103 ''смартфонів'':19B,27C,177 ''сторінці'':84 ''суперник'':149 ''тестуван'':136 ''технологі'':15B,23C,58 ''технологіє'':169 ''технології'':37,80 ''то'':151 ''україн'':4A ''хвилин'':147 ''ході'':135 ''час'':153 ''швидкості'':100 ''швидкої'':17B,25C ''швидкіст'':65 ''що'':166 ''як'':73 ''ємності'':67 ''ємністю'':121 ''і'':110,124 ''імовірн'':113', 'девайс, новини, новини України, новини дня, головні новини, свіжі новини, новини Київ', 1, 1, 0, 0, 1, 'https://www.unian.ua/m/economics/telecom/10493268-xiaomi-predstavila-tehnologiyu-rekordno-shvidkoji-zaryadki-smartfoniv-video.html
