INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (19, 'Підтримуємо наших: відомий черкаський репер номінується на престижну музпремію', 'ef872c73cded7e3da4fa4665c4a03664', 'Підтримуємо наших: відомий черкаський репер номінується на престижну музпремію', 'Відомий черкаський репер, екс-учасник гурту “На Відміну Від” Андрій Фріл номінується на “Jager Music Awards” – першу премію незалежної музики, пише ВІККА. Його кандидатуру висунули на звання кращого в номінації “Хіп-хоп-артист року”. Нагадаємо, що у 2018 році Фріл випустив сольний альбом “Спалах”, що увійшов у топ-10 кращих альбомів усіх жанрів 2018 року – за версією музичної премії “APrize”. Окрім цього, один з найавторитетніших музичних сайтів України “LiRoom” визнав центральну пісню альбому “Спалах” кращим треком року. Підтримати Андрія Фріла можна за посиланням, обравши категорію “Хіп-хоп-артист року”. Зазначимо, що церемонія нагородження другої премії незалежної музики “Jager Music Awards 2019” відбудеться 15-го листопада в арт-галереї LAVRA. Основних номінацій десять: “Гурт року”, “Сингл року”, “Відео року”, “Хіп-хоп артист року”, “Рок-артист року”, “Електронний артист року”, “Лейбл року”, “Подія року”, “Діджей року” та “Внесок в індустрію”.', '<p class=\"bb_p\">Відомий черкаський репер, екс-учасник гурту “На Відміну Від” Андрій Фріл номінується на “Jager Music Awards” – першу премію незалежної музики, пише ВІККА.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-38-08_786.png" alt="" title="" /></p>
