INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (20, 'Черкаські рятувальники ліквідували три пожежі спричинені необережним поводженням з вогнем', 'fcebd0e7894444cfc6ad497a8cc7178f', 'Черкаські рятувальники ліквідували три пожежі спричинені необережним поводженням з вогнем', '30 жовтня о 15:45 на території приватного домоволодіння на вулиці Польовій у селі Червона Слобода Черкаського району, виникла пожежа у надвірній споруді. Повідомили у ДСНС Черкащини. На боротьбу з вогнем оперативно прибули рятувальники 1-ї державної пожежно-рятувальної частини міста Черкаси та місцева пожежна команда із села Леськи. Спільними зусиллями вогнеборців пожежу о 16:11 вдалося локалізувати, а о 17:03 ліквідувати. До гасіння залучалися 2 одиниці спецтехніки та 5 чоловік особового складу. Вогнем пожежі знищено 2 м.куб. дров, що зберігалися в середині споруди. За попередніми даними, причиною пожежі стало необережне поводження з вогнем. З аналогічної причини, вночі 31 жовтня двічі горіло сміття в місті Черкаси. Завдяки професійним діям рятувальників 3-ї та 2-ї пожежно-рятувальних частин пожежі вдалося успішно ліквідувати та запобігти подальшому поширенню вогню.', '<p class=\"bb_p\">30 жовтня о 15:45 на території приватного домоволодіння на вулиці Польовій у селі Червона Слобода Черкаського району, виникла пожежа у надвірній споруді. Повідомили у ДСНС Черкащини.</p>
