INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (23, 'Рятувальники ліквідували дві пожежі на відкритих територіях', '6dc81fc24da7c01b340f5c3721532901', 'Рятувальники ліквідували дві пожежі на відкритих територіях', 'Впродовж доби 30 жовтня рятувальники Черкащини двічі виїжджали на ліквідацію пожеж на відкритих територіях. Повідомили у ДСНС Черкащини. Перший випадок стався о 10:20 у селі Чорнявка Черкаського району, де виникла пожежа сухої трави на площі 2 га. Пожежу о 14:00 локалізували, а о 15:30 ліквідували рятувальники 1-ї державної пожежно-рятувальної частини міста Черкаси, які за викликом прибули на місце події. У другому випадку пожежа виникла на полі у селі Паланка Уманського району. На боротьбу з вогнем, що охопив стерню кукурудзи на площі понад 4 га прибули рятувальники уманської пожежно-рятувальної частини та місцевої пожежної команди. Завдяки спільним зусиллям вогнеборців пожежу о 16:30 вдалося локалізувати, а о 17:25 ліквідувати. За попередніми даними, причиною пожеж в обох випадках стало необережне поводження з вогнем.', '<p class=\"bb_p\">Впродовж доби 30 жовтня рятувальники Черкащини двічі виїжджали на ліквідацію пожеж на відкритих територіях. Повідомили у ДСНС Черкащини.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-38-22_929.png" alt="" title="" /></p>
