INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (52, 'Юні уманчани здобули ІІІ місце на Всеукраїнському фестивалі «Козацький гарт»', '18ed4ca65ee11f68c8e912f48fb3831f', 'Юні уманчани здобули ІІІ місце на Всеукраїнському фестивалі «Козацький гарт»', 'З 7 по 11 жовтня в місті Галич Івано-Франківської області проходили фінальні змагання Всеукраїнського етапу фізкультурно-патріотичного фестивалю школярів «Козацький гарт 2019». У змаганнях брали участь команди з десяти областей України. Черкаську область представляла команда юних козаків Уманської загальноосвітньої школи І-ІІІ ступенів №3 під керівництвом вчителя фізичної культури Олексія Шеленкова. Як повідомили у виконавчому комітеті Уманської міської ради, за результатами турніру уманчани посіли ІІІ місце. Склад команди юних козаків Уманської ЗОШ №3: Карина Іванюк, учениця 5 класу;Олександр Заводянський, учень 5 класу;Марія Поліщук, учениця 6 класу;Михайло Овчарук, учень 6 класу;Юлія Березницька, учениця 7 класу;Михайло Пестерев, учень 7 класу;Богдан Мельник, учень 8 класу;Анна Зозуля, учениця 8 класу;Софія Коваленко, учениця 9 класу;Дмитро Гріднєв, учень 9 класу;Анастасія Дячок, учениця 10 класу;Олександр Осичка, учень 10 класу.', '<p class=\"bb_p\"><b class=\"bb_b\">З 7 по 11 жовтня в місті Галич Івано-Франківської області проходили фінальні змагання Всеукраїнського етапу фізкультурно-патріотичного фестивалю школярів «Козацький гарт 2019». У змаганнях брали участь команди з десяти областей України.</b></p>
