INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (84, 'Іще один шкільний автобус отримали школярі Тальнівщини', '8c3afe03f2eac0e8173b2aee67e24fa5', 'Іще один шкільний автобус отримали школярі Тальнівщини', 'На Тальнівщині отримали ще один шкільний автобус, який використовуватимуть для підвезення дітей з села Раманівка до  Мошурівської ЗОШ І-ІІІ ст. Вартість транспорту – 1 млн 855 тис. грн. Автобус придбали на умовах співфінансування (50х50) з державного та районного бюджетів за сприяння депутатів Черкаської обласної ради.  Депутати районної ради також підтримали ініціативу щодо виділення 925 тис грн із районного бюджету. Новий шкільний автобус &nbsp;«Еталон» розрахований на 30 місць, має додаткові рівні безпеки, зокрема обмеження у швидкості руху, звукову та світлову сигналізацію, аварійні кнопки, спеціальні сидіння та зручні сходинки.&nbsp;Тож школярі матимуть можливість&nbsp;зручно і комфортно&nbsp;добиратися&nbsp;до навчального закладу. За інформацією Тальнівської районної адміністрації', '<p class=\"bb_p\"><b class=\"bb_b\">На Тальнівщині отримали ще один шкільний автобус, який використовуватимуть для підвезення дітей з села Раманівка до  Мошурівської ЗОШ І-ІІІ ст.</b></p>
