INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (93, 'У Каневі в автівці виявили двох дітей: дівчинка загинула, хлопець – в реанімації', 'b5f3083229500abac24f7397a767c7e9', 'У Каневі в автівці виявили двох дітей: дівчинка загинула, хлопець – в реанімації', 'Коли медики прибули на місце події, то 15-річна дівчинка вже перебувала у стані біологічної смерті, до того ж уже давно. А хлопця доправили до реанімації у вкрай важкому стані. Про це повідомиларечниця КУ “Обласний центр екстреної медичної допомоги та медицини катастроф ЧОР” Ірина Назарок. За попередньою інформацією, діти отруїлись чадним газом. Подія трапилася сьогодні, 31 жовтня.', '<p class=\"bb_p\"><b class=\"bb_b\">Коли медики прибули на місце події, то 15-річна дівчинка вже перебувала у стані біологічної смерті, до того ж уже давно. А хлопця доправили до реанімації у вкрай важкому стані.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_20-41-53_28.png" alt="" title="" /></p>
