INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (110, 'Уманська “швидка” потрапила в ДТП', 'e61d260623b86df105cca76325fe7dc9', 'Уманська “швидка” потрапила в ДТП', 'Бригада екстреної медичної допомоги Уманської підстанції СШМД поверталася з виклику. На трасі поблизу Умані водія засліпила зустрічна автівка, після чого відбулося зіткнення з легковим автомобілем. Щоб уникнути лобового удару, водій «швидкої» різко повернув руль управо, після чого авто перекинулось.Про це повідомляє КУ “Обласний центр екстреної медичної допомоги та медицини катастроф ЧОР”.Бригада ЕМД складалась з водія та двох медичних працівників. Наразі одного медпрацівника доправлено в Уманську міську лікарню з підозрою на струс головного мозку. В легковому автомобілі постраждалих немає.', '<p class=\"bb_p\">Бригада екстреної медичної допомоги Уманської підстанції СШМД поверталася з виклику. На трасі поблизу Умані водія засліпила зустрічна автівка, після чого відбулося зіткнення з легковим автомобілем. Щоб уникнути лобового удару, водій «швидкої» різко повернув руль управо, після чого авто перекинулось.</p><p class=\"bb_p\">Про це повідомляє КУ “Обласний центр екстреної медичної допомоги та медицини катастроф ЧОР”.</p><p class=\"bb_p\">Бригада ЕМД складалась з водія та двох медичних працівників. Наразі одного медпрацівника доправлено в Уманську міську лікарню з підозрою на струс головного мозку. В легковому автомобілі постраждалих немає.</p>
