INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (136, 'Уночі в Черкасах двічі горіло сміття', 'cc17fc2d279a8b03b26506a02641f554', 'Уночі в Черкасах двічі горіло сміття', '30 жовтня на території приватного домоволодіння на вулиці Польовій у селі Червона Слобода Черкаського району, виникла пожежа у надвірній споруді. Про це повідомляє Управління ДСНС України в Черкаській області. Вогнем пожежі знищено 2 м.куб. дров, що зберігалися в середині споруди. За попередньою інформацією, причиною пожежі стало необережне поводження з вогнем. З аналогічної причини, вночі 31 жовтня двічі горіло сміття в місті Черкаси. Завдяки  професійним діям рятувальників 3-ї та 2-ї пожежно-рятувальних частин пожежі вдалося успішно ліквідувати та запобігти подальшому поширенню вогню.', '<p class=\"bb_p\">30 жовтня на території приватного домоволодіння на вулиці Польовій у селі Червона Слобода Черкаського району, виникла пожежа у надвірній споруді. Про це повідомляє Управління ДСНС України в Черкаській області.</p>
