INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (140, 'У Департаменті соцзахисту роз’яснили оновлений порядок надання субсидій', 'a9b193a505195551766281c9a2ed7e3d', 'У Департаменті соцзахисту роз’яснили оновлений порядок надання субсидій', 'Починаючи від 20 жовтня спрощено умови призначення житлових субсидій окремим категоріям громадян відповідно до Урядової постанови. Їх роз’яснили в Департаменті соціального захисту населення ОДА. Зокрема, надано можливість призначення субсидії дитячим будинкам сімейного типу на понаднормову площу житла. Разом із тим, при призначенні їм субсидії до доходу не враховуються матеріальна допомога, яка виплачується за рахунок коштів державного або місцевого бюджетів, та стипендія на дітей-сиріт і дітей, позбавлених батьківського піклування, а також придбані ними транспорті засоби. Постановою також передбачено, що під час призначення житлової субсидії не враховуються доходи одного з подружжя, якщо їм обом більше 60 років і вони проживають у сільській місцевості або селищах міського типу, у разі коли їх зареєстроване (фактичне) місце проживання відмінне від адреси домогосподарства. Субсидія військовослужбовцям призначатиметься без отримання інформації про сплату ними єдиного внеску на загальнообов’язкове державне соціальне страхування. За наявності причепа, з дати випуску якого минуло менше 5 років, домогосподарство матиме право на субсидію. Перелічені зміни вже застосовуються при визначенні права на призначення субсидії зазначеним категоріям громадян. Але слід пам’ятати, що право осіб, яким субсидія призначена до введення цих змін, не переглядається. Однак у разі звернення до органів соціального захисту населення субсидія їм може бути призначена з урахуванням прийнятих змін.', '<p class=\"bb_p\"><b class=\"bb_b\">Починаючи від 20 жовтня спрощено умови призначення житлових субсидій окремим категоріям громадян відповідно до Урядової постанови. Їх роз’яснили в Департаменті соціального захисту населення ОДА.</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_21-49-03_706.png" alt="" title="" /></p>
