INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (144, 'Школярі Тальнівщини отримали новий шкільний автобус', 'fe0edb7f13d7429603db387c1a7ce6b6', 'Школярі Тальнівщини отримали новий шкільний автобус', 'Школярі на Тальнівщині отримали ще один автобус, який використовуватимуть для підвезення дітей з села Раманівка до  Мошурівської ЗОШ І-ІІІ ст. Вартість транспорту – 1 млн 855 тис. грн. Автобус придбали на умовах співфінансування (50х50) з державного та районного бюджетів за сприяння депутатів Черкаської обласної ради.  Депутати районної ради також підтримали ініціативу щодо виділення 925 тис грн з районного бюджету. Новий шкільний автобус  «Еталон» розрахований на 30 місць, має додаткові рівні безпеки, зокрема обмеження у швидкості руху, звукову та світлову сигналізацію, аварійні кнопки, спеціальні сидіння та зручні сходинки. Тож школярі матимуть можливість зручно і комфортно добиратися до навчального закладу. За інформацією Тальнівської районної адміністрації.', '<p class=\"bb_p\">Школярі на Тальнівщині отримали ще один автобус, який використовуватимуть для підвезення дітей з села Раманівка до  Мошурівської ЗОШ І-ІІІ ст. Вартість транспорту – 1 млн 855 тис. грн. Автобус придбали на умовах співфінансування (50х50) з державного та районного бюджетів за сприяння депутатів Черкаської обласної ради.  Депутати районної ради також підтримали ініціативу щодо виділення 925 тис грн з районного бюджету.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-10-31/2019-10-31_22-01-49_597.png" alt="" title="" /></p>
