INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (173, 'Заступник мера розказав про бісівське одягання і диявольські символи', '6300552872ff00a9e2d66fee7e2bf12a', 'Заступник мера розказав про бісівське одягання і диявольські символи', 'Заступник міського голови Ігор Волошин у своєму «Фейсбуці» поділився думками щодо святкування Хелловіну. Зокрема, він назвав свято «навіюванням Америки» і «святом нечисті». «Я категорично проти святкування Хелловіну. Я не проти здорових веселощів і розваг, я не проти «чужих» свят, хоч і своїх достатньо, я не проти заробітків для бізнесу. Я проти прикликання у свої домівки і міста нечистої сили, я проти бісівського зодягання і нанесення на свої тіла диявольських символів і знаків. Дехто Хелловін називає безневинним святом із переодяганням, ляканням людей, вимаганням цукерків, відлякуванням духів, вирізанням гарбуза зі свічкою, для мене ж це свято нечисті», – написав Волошин. Зазначимо, зо Хелловін, або Вечір усіх святих відзначається у ніч проти 1 листопада. Свято особливо популярне в США.', '<p class=\"bb_p\"><b class=\"bb_b\">Заступник міського голови Ігор Волошин у своєму «Фейсбуці» поділився думками щодо святкування Хелловіну. Зокрема, він назвав свято «навіюванням Америки» і «святом нечисті».</b></p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-11-01/2019-11-01_11-15-05_709.png" alt="" title="" /></p>
