INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (181, 'Завтра у Черкасах відкриють колесо огляду', 'e7b4e4b0b69054cef0faf28f384d974f', 'Завтра у Черкасах відкриють колесо огляду', 'Вже відзавтра атракціон “Колесо огляду” запрацює для відвідувачів парку Сосновий бір. Про це повідомив директор КП “Дирекція парків” Андрій Куча. – Вчора отримали сертифікат на введення в експлуатацію “Колеса огляду”. Це дозволяє нам відкрити його для відвідувачів, – говорить Андрій Куча. Пробну поїздку вже здійснилиЗа йогос словами, працюватиме колесо і при мінусових температурах. Дорослий квиток коштуватиме 50 грн, дитячий – 30. Працюватиме атракціон з 10 до 17 години. Керівник КП “Дирекція парків” Андрій Куча протестував колесо на собіЗа умови, що охочих прокататися на новому атракціоні буде більше, час його роботи продовжуватимуть. Не пропустіть найважливішого - отримуєте push-сповіщення від команди %SITENAME%', '<p class=\"bb_p\"><b class=\"bb_b\">Вже відзавтра атракціон “Колесо огляду” запрацює для відвідувачів парку Сосновий бір.</b></p>
