INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (194, 'В Смілянському районі поліцейські виявили факт незаконного перевезення деревини', '5c24667cbfb686da8ed1c0895a9d766e', 'В Смілянському районі поліцейські виявили факт незаконного перевезення деревини', 'Під час профілактичного відпрацювання території міста Сміла та Смілянського району на автодорозі Сміла-Умань правоохоронці зупинили автомобіль, який перевозив деревину. Під час перевірки було встановлено, що водій транспортного засобу,  перевозив деревину, яка мала невідповідність електронних бирок та дозвільних документів. На місце події викликано слідчо-оперативну групу. Повідомили у нацполіції Черкащини. Автомобіль та деревину вилучено. Смілянським відділом поліції відкрито кримінальне провадження за частиною 4 статті 358 (Підроблення документів, печаток, штампів та бланків, збут чи використання підроблених документів, печаток, штампів) Кримінального кодексу України. Вирішується питання щодо накладення арешту на вилучене майно.  ', '<p class=\"bb_p\">Під час профілактичного відпрацювання території міста Сміла та Смілянського району на автодорозі Сміла-Умань правоохоронці зупинили автомобіль, який перевозив деревину. Під час перевірки було встановлено, що водій транспортного засобу,  перевозив деревину, яка мала невідповідність електронних бирок та дозвільних документів. На місце події викликано слідчо-оперативну групу. Повідомили у нацполіції Черкащини.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-11-01/2019-11-01_14-05-02_697.png" alt="" title="" /></p>
<p class=\"bb_p\">Автомобіль та деревину вилучено. Смілянським відділом поліції відкрито кримінальне провадження за частиною 4 статті 358 (Підроблення документів, печаток, штампів та бланків, збут чи використання підроблених документів, печаток, штампів) Кримінального кодексу України. Вирішується питання щодо накладення арешту на вилучене майно.</p>
