INSERT INTO "cknews.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (198, 'Завтра на Черкащині очікуються пориви вітру', '26524191a435abb790ef2b91cc110124', 'Завтра на Черкащині очікуються пориви вітру', 'Завтра, 2 листопада, на Черкащині очікуються пориви вітру 15-20 м/с (перший рівень небезпечності). Про це повідомляє пресслужба Черкаського обласного гідрометцентру. Черкащанам нагадують, що робити під час шторму чи урагану: зачиніть вікна та відійдіть від них подалі; загасіть вогонь у грубах, вимкніть електро- та газо- постачання; сховайтеся у внутрішніх приміщеннях – коридорі, ванній кімнаті, коморі або підвалі; не намагайтесь перейти в іншу будівлю – це небезпечно; не користуйтеся ліфтами, електромережу можуть раптово вимкнути; обминайте хиткі будівлі та будинки з хитким дахом, якщо лихо застало вас на вулиці, заховайтеся в підвал найближчого будинку; якщо ви на відкритій місцевості, притисніться до землі на дні яру, канави, кювету, захищаючи голову одягом чи гілками дерев; зупиніться, якщо ви їдете автомобілем. Не ховайтеся у ньому, а виходьте і швидко ховайтеся у міцній будівлі або на дні будь-якого заглиблення; уникайте мостів, естакад, трубопроводів, ліній електромереж, водойм, потенційно небезпечних промислових об’єктів та дерев; не наближайтесь до води подивитися на шторм.', '<p class=\"bb_p\">Завтра, 2 листопада, на Черкащині очікуються пориви вітру 15-20 м/с (перший рівень небезпечності). Про це повідомляє пресслужба Черкаського обласного гідрометцентру.</p>
<p class=\"bb_p align_center\"><img class="post_img" src="/uploads/images/2019-11-01/2019-11-01_14-58-11_10.png" alt="" title="" /></p>
<p class=\"bb_p\">Черкащанам нагадують, що робити під час шторму чи урагану:</p>
<p class=\"bb_p\">зачиніть вікна та відійдіть від них подалі;</p>
<p class=\"bb_p\">загасіть вогонь у грубах, вимкніть електро- та газо- постачання;</p>
<p class=\"bb_p\">сховайтеся у внутрішніх приміщеннях – коридорі, ванній кімнаті, коморі або підвалі;</p>
<p class=\"bb_p\">не намагайтесь перейти в іншу будівлю – це небезпечно;</p>
<p class=\"bb_p\">не користуйтеся ліфтами, електромережу можуть раптово вимкнути;</p>
<p class=\"bb_p\">обминайте хиткі будівлі та будинки з хитким дахом, якщо лихо застало вас на вулиці, заховайтеся в підвал найближчого будинку;</p>
<p class=\"bb_p\">якщо ви на відкритій місцевості, притисніться до землі на дні яру, канави, кювету, захищаючи голову одягом чи гілками дерев;</p>
<p class=\"bb_p\">зупиніться, якщо ви їдете автомобілем. Не ховайтеся у ньому, а виходьте і швидко ховайтеся у міцній будівлі або на дні будь-якого заглиблення;</p>
<p class=\"bb_p\">уникайте мостів, естакад, трубопроводів, ліній електромереж, водойм, потенційно небезпечних промислових об’єктів та дерев;</p>
<p class=\"bb_p\">не наближайтесь до води подивитися на шторм.</p><div class="source"><p class=\"bb_p align_right\"><a rel="nofollow noreferrer" href="http://vycherpno.ck.ua/zavtra-na-cherkashhini-ochikuyutsya-porivi-vitru/" target="_blank">За матеріалами "vycherpno.ck.ua"</a></p></div>', 1, '2019-11-01 14:58:15.614778', '''-20'':26 ''15'':25 ''2'':18 ''align'':43,226 ''bb'':15,41,47,60,71,84,98,110,121,144,167,195,213,224 ''center'':44 ''class'':14,40,46,59,70,83,97,109,120,143,166,194,212,223 ''p'':13,16,39,42,45,48,58,61,69,72,82,85,96,99,108,111,119,122,142,145,165,168,193,196,211,214,222,225 ''right'':227 ''vycherpno.ck.ua'':230 ''аб'':94,186 ''автомобіл'':173 ''буд'':190 ''будинк'':127,141 ''будь-як'':189 ''будівл'':105 ''будівлі'':125,185 ''ванні'':91 ''ви'':147,171 ''вимкнут'':118 ''вимкніт'':77 ''виходьт'':179 ''внутрішніх'':88 ''вогон'':74 ''вод'':218 ''водойм'':203 ''вулиці'':136 ''від'':66 ''відкриті'':149 ''відійдіть'':65 ''вікна'':63 ''вітру'':6B,12C,24 ''газ'':80 ''голов'':160 ''груб'':76 ''гідрометцентр'':38 ''гілкам'':163 ''дах'':130 ''дер'':164,210 ''дні'':155,188 ''електр'':78 ''електромереж'':115,202 ''естакад'':199 ''з'':128 ''завтр'':1B,7C,17 ''загасіт'':73 ''заглиблен'':192 ''заста'':133 ''захищаюч'':159 ''захова'':137 ''зачиніт'':62 ''землі'':153 ''зупиніт'':169 ''канав'':157 ''коморі'':93 ''коридорі'':90 ''корист'':113 ''кювет'':158 ''кімнаті'':92 ''листопад'':19 ''лих'':132 ''ліній'':201 ''ліфтам'':114 ''м'':27 ''матеріал'':229 ''можут'':116 ''мостів'':198 ''місцевості'':150 ''міцній'':184 ''наближа'':216 ''нагадуют'':50 ''найближч'':140 ''намага'':101 ''небезпечн'':107,205 ''небезпечності'':31 ''ньом'':177 ''обласн'':37 ''обмина'':123 ''одяг'':161 ''очікуют'':4B,10C,22 ''перейт'':102 ''перш'':29 ''повідомляє'':34 ''подалі'':68 ''подивит'':219 ''порив'':5B,11C,23 ''постачан'':81 ''потенційн'':204 ''пресслужб'':35 ''приміщен'':89 ''притисніт'':151 ''промислов'':206 ''під'':53 ''підвал'':139 ''підвалі'':95 ''раптов'':117 ''робит'':52 ''рівен'