INSERT INTO "it-news.pp.ua"."posts" ("id", "title", "alt_title", "descr", "short_post", "full_post", "author_id", "created_time", "svector", "keywords", "category", "posted", "fixed", "static", "repost_tg", "comment") VALUES (53, 'Apple таємно розробляє сенсорний гібридний комп’ютер з підтримкою LTE – ЗМІ', '5b282a65a1df67940df142a4b45991fe', 'Apple таємно розробляє сенсорний гібридний комп’ютер з підтримкою LTE – ЗМІ', 'Серед відомих характеристик пристрою: сенсорний екран, слот для SIM-карти, GPS, компас, захист від води, а також робота за протоколом EFI. Компанія Apple працює над новим пристроєм під назвою Apple Star, який може стати першим портативним комп’ютером Mac з ARM-процесором. Як повідомляє tokar.ua, розробка нового комп’ютера ще не готова до масового виробництва. Виданню 9to5Mac стало відомо, що прототипи з січня 2018-го року виготовляює китайська компанія Pegatron , і нещодавно перша партія девайсів була доставлена до США для тестування працівниками Apple. Зазначається, що до 2006 року Mac використовував для своїх комп’ютерів процесори Intel, поступово відмовляючись від них. Так, з 2010 року всі мобільні пристрої Apple (планшети та смартфони) використовують процесори власної розробки Apple. Останнім часом у західних часто ЗМІ повідомлялося, що Apple планує повністю перейти на використання власних процесорів вже до 2020 року. Девайс, що розробляється, вважають представником нового сімейства пристроїв, яке працюватиме на операційній системі, створеній на основі iOS. Серед відомих характеристик нового пристрою: сенсорний екран, слот для SIM-карти, GPS, компас, захист від води, а також робота за протоколом EFI. Саме EFI (система завантаження, яка використовується в Mac, аналог BIOS) дасть можливість девайсам Star бути першими комп’ютерами Mac на базі архітектури ARM. Як повідомляв УНІАН, за словами інсайдерів, у цьому році Apple готує випуск цілої серії гаджетів з функцією Face ID. Поки функція розпізнавання обличчя доступна тільки для iPhone X.', '<p clas